    // Create trips one by one
    for (const trip of trips) {
      try {
        const result = await sql`
          INSERT INTO trips (
            creator_id, title, destination_id, description, 
            start_date, end_date, budget, max_participants, 
            travel_style, status, current_participants
          ) VALUES (
