async function addActivitiesForAllDestinations() {
  try {
    console.log('ðŸŒŸ ADICIONANDO ATIVIDADES PARA TODOS OS DESTINOS...\n');

    let totalAdded = 0;

    for (const [destination, destinationActivities] of Object.entries(DESTINATION_ACTIVITIES)) {
      console.log(`ðŸ“ Adicionando atividades para ${destination}:`);

      for (const activityData of destinationActivities) {
        try {
          // Verificar se a atividade jÃ¡ existe
          const existing = await db.select()
            .from(activities)
            .where(eq(activities.title, activityData.title))
            .limit(1);

          if (existing.length > 0) {
            console.log(`  âš ï¸  Atividade "${activityData.title}" jÃ¡ existe`);
            continue;
          }

          // Inserir atividade
          const result = await db.insert(activities).values(activityData);
          const activityId = result.insertId;

          console.log(`  âœ… Atividade "${activityData.title}" criada (ID: ${activityId})`);

          // Adicionar propostas de orÃ§amento
          const proposals = [
            {
              activityId: Number(activityId),
              title: 'EconÃ´mico',
              price: 80,
              inclusions: ['Guia local', 'Seguro'],
              exclusions: ['Transporte', 'AlimentaÃ§Ã£o'],
              description: 'OpÃ§Ã£o bÃ¡sica com o essencial'
            },
            {
              activityId: Number(activityId),
              title: 'Completo',
              price: 150,
              inclusions: ['Guia especializado', 'Transporte', 'Seguro', 'Lanche'],
              exclusions: ['AlimentaÃ§Ã£o completa'],
              description: 'ExperiÃªncia completa com conforto'
            },
            {
              activityId: Number(activityId),
              title: 'Premium',
              price: 250,
              inclusions: ['Guia especializado', 'Transporte executivo', 'Seguro', 'AlimentaÃ§Ã£o completa', 'Fotos profissionais'],
              exclusions: [],
              description: 'ExperiÃªncia de luxo com todos os extras'
            }
          ];

          for (const proposal of proposals) {
            await db.insert(activityBudgetProposals).values({
              ...proposal,
              inclusions: JSON.stringify(proposal.inclusions),
              exclusions: JSON.stringify(proposal.exclusions)
            });
          }

          console.log(`    ðŸ’° 3 propostas de orÃ§amento adicionadas`);
          totalAdded++;

        } catch (error) {
          console.error(`  âŒ Erro ao adicionar "${activityData.title}":`, error);
        }
      }
    }

    console.log(`\nâœ… Processo concluÃ­do! ${totalAdded} novas atividades adicionadas.`);

    // Verificar resultado final
    console.log('\nðŸ” VERIFICANDO CORRESPONDÃŠNCIAS FINAIS...');

    const allActivities = await db.select().from(activities);
    const activityLocations = [...new Set(allActivities.map(a => a.location))].filter(Boolean);

    console.log('\nðŸ›ï¸ LOCALIZAÃ‡Ã•ES DAS ATIVIDADES (apÃ³s adiÃ§Ã£o):');
    activityLocations.forEach(loc => {
      const count = allActivities.filter(a => a.location === loc).length;
      console.log(`  - "${loc}" (${count} atividades)`);
    });

    // Verificar quais destinos ainda precisam de atividades
    const tripsResult = await db.execute(`
      SELECT DISTINCT destination 
      FROM trips 
      WHERE destination IS NOT NULL 
        AND destination != 'undefined' 
        AND destination != ''
    `);

    const tripDestinations = (tripsResult as any).map(row => row.destination).filter(Boolean);

    console.log('\nðŸ“ DESTINOS DAS VIAGENS:');
    tripDestinations.forEach(dest => console.log(`  - "${dest}"`));

    const matched = activityLocations.filter(loc => tripDestinations.includes(loc));
    const missingActivities = tripDestinations.filter(dest => !activityLocations.includes(dest));

    console.log(`\nâœ… Destinos com atividades: ${matched.length}`);
    matched.forEach(loc => console.log(`  âœ“ "${loc}"`));

    console.log(`\nðŸš¨ Destinos ainda sem atividades: ${missingActivities.length}`);
    missingActivities.forEach(dest => console.log(`  âš ï¸ "${dest}"`));

  } catch (error) {
    console.error('âŒ Erro durante processo:', error);
  }

  process.exit(0);
}

addActivitiesForAllDestinations();

