#!/usr/bin/env tsx

import { db } from "./db";
import { sql } from "drizzle-orm";

async function addDestinationColumnToActivities() {
  console.log("ðŸš€ Adicionando coluna destination_id Ã  tabela activities...");

  try {
    // 1. Verificar se a coluna jÃ¡ existe
    console.log("ðŸ” Verificando estrutura da tabela activities...");
    const columns = await db.execute(sql`SHOW COLUMNS FROM activities LIKE 'destination_id'`);

    if (columns[0] && (columns[0] as any[]).length > 0) {
      console.log("â„¹ï¸ Coluna destination_id jÃ¡ existe em activities");
    } else {
      console.log("âž• Adicionando coluna destination_id...");
      await db.execute(sql`ALTER TABLE activities ADD COLUMN destination_id INT NOT NULL DEFAULT 1`);
      console.log("âœ… Coluna destination_id adicionada");
    }

    // 2. Verificar se hÃ¡ atividades Ã³rfÃ£os agora
    console.log("ðŸ” Verificando activities Ã³rfÃ£os...");
    const orphanActivities = await db.execute(sql`
      SELECT a.id, a.title, a.destination_id 
      FROM activities a 
      LEFT JOIN destinations d ON a.destination_id = d.id 
      WHERE d.id IS NULL
      LIMIT 5
    `);

    const activityRows = orphanActivities[0] as any[];
    console.log(`ðŸ“Š Activities Ã³rfÃ£os encontrados: ${activityRows.length}`);

    // 3. Corrigir activities Ã³rfÃ£os se existirem
    if (activityRows.length > 0) {
      for (const activityData of activityRows) {
        console.log(`ðŸ”§ Corrigindo activity ID ${activityData.id}: ${activityData.title}`);
        await db.execute(sql`UPDATE activities SET destination_id = 1 WHERE id = ${activityData.id}`);
      }
      console.log("âœ… Activities Ã³rfÃ£os corrigidos");
    }

    // 4. Tentar adicionar foreign key para activities
    console.log("ðŸ”— Adicionando foreign key para activities...");
    try {
      await db.execute(sql`ALTER TABLE activities ADD CONSTRAINT activities_destinations_fk FOREIGN KEY (destination_id) REFERENCES destinations(id)`);
      console.log("âœ… FK activities_destinations_fk adicionada");
    } catch (e: any) {
      if (e.message.includes('already exists') || e.message.includes('duplicate')) {
        console.log("â„¹ï¸ FK activities jÃ¡ existe");
      } else {
        console.log("âš ï¸ Erro FK activities:", e.message.substring(0, 100));
      }
    }

    console.log("ðŸŽ‰ MigraÃ§Ã£o de activities concluÃ­da!");

  } catch (error) {
    console.error("âŒ Erro:", error);
    throw error;
  }
}

// Executar
addDestinationColumnToActivities()
  .then(() => {
    console.log("âœ… Sucesso!");
    process.exit(0);
  })
  .catch((error) => {
    console.error("âŒ Falha:", error);
    process.exit(1);
  });

