async function addGramadoActivitiesFixed() {
  console.log('ðŸŽ¿ Iniciando cadastro das atividades de Gramado (versÃ£o corrigida)...');

  try {
    const insertedActivities = [];

    // Inserir atividades uma por uma
    for (const activity of gramadoActivities) {
      const [result] = await db.insert(activities).values(activity);
      console.log(`âœ… Atividade "${activity.title}" cadastrada com sucesso`);

      // Buscar ID da atividade inserida
      const [insertedActivity] = await db.select().from(activities)
        .where(eq(activities.title, activity.title))
        .orderBy(desc(activities.id))
        .limit(1);

      insertedActivities.push(insertedActivity);
    }

    // Inserir propostas de orÃ§amento
    let proposalIndex = 0;
    for (let i = 0; i < insertedActivities.length; i++) {
      const activity = insertedActivities[i];

      // Cada atividade tem 3 propostas
      for (let j = 0; j < 3; j++) {
        const proposal = {
          ...budgetProposals[proposalIndex],
          activityId: activity.id
        };

        await db.insert(activityBudgetProposals).values(proposal);
        console.log(`ðŸ’° Proposta "${proposal.title}" criada para ${activity.title}`);
        proposalIndex++;
      }
    }

    console.log('ðŸŽ‰ Todas as atividades de Gramado foram adicionadas com sucesso!');
    console.log(`ðŸ“Š Total: ${insertedActivities.length} atividades, ${budgetProposals.length} propostas`);

  } catch (error) {
    console.error('âŒ Erro ao cadastrar atividades:', error);
    throw error;
  }
}

// Executar se chamado diretamente
if (import.meta.url === `file://${process.argv[1]}`) {
  addGramadoActivitiesFixed()
    .then(() => process.exit(0))
    .catch((error) => {
      console.error('âŒ Falha na execuÃ§Ã£o:', error);
      process.exit(1);
    });
}

export { addGramadoActivitiesFixed };

