  // ==================== CRUZEIROS ====================
  "Cruzeiro MediterrÃ¢neo": { countryType: "cruzeiro", region: "MediterrÃ¢neo", city: "MÃºltiplas Cidades", route: "Espanha-FranÃ§a-ItÃ¡lia-GrÃ©cia" },
  "Cruzeiro Caribe": { countryType: "cruzeiro", region: "Caribe", city: "MÃºltiplas Ilhas", route: "Bahamas-Jamaica-MÃ©xico" },
  "Cruzeiro Fiordos Noruegueses": { countryType: "cruzeiro", region: "Norte da Europa", city: "Fiordos", route: "Noruega-SuÃ©cia-Dinamarca" },
  "Cruzeiro Costa Brasileira": { countryType: "cruzeiro", region: "Costa AtlÃ¢ntica", city: "Litoral Brasileiro", route: "Santos-Rio-Salvador-Recife" },
  "Cruzeiro AntÃ¡rtica": { countryType: "cruzeiro", region: "AntÃ¡rtica", city: "Continente AntÃ¡rtico", route: "Ushuaia-PenÃ­nsula AntÃ¡rtica" },
  "Cruzeiro Rio Amazonas": { countryType: "cruzeiro", region: "AmazÃ´nia", city: "Rio Amazonas", route: "Manaus-SantarÃ©m-BelÃ©m" },
};

// Helper function to intelligently parse location and create mapping
function parseLocation(location: string): { countryType: string; region: string; city: string } {
  const loc = location.trim();

  // Check Brazilian state abbreviations
  const brazilianStates = ['AC', 'AL', 'AP', 'AM', 'BA', 'CE', 'DF', 'ES', 'GO', 'MA', 'MT', 'MS', 'MG', 'PA', 'PB', 'PR', 'PE', 'PI', 'RJ', 'RN', 'RS', 'RO', 'RR', 'SC', 'SP', 'SE', 'TO'];
  const stateRegionMap = {
    'AC': 'Norte', 'RO': 'Norte', 'AM': 'Norte', 'RR': 'Norte', 'PA': 'Norte', 'AP': 'Norte', 'TO': 'Norte',
    'MA': 'Nordeste', 'PI': 'Nordeste', 'CE': 'Nordeste', 'RN': 'Nordeste', 'PB': 'Nordeste', 'PE': 'Nordeste', 'AL': 'Nordeste', 'SE': 'Nordeste', 'BA': 'Nordeste',
    'MT': 'Centro-Oeste', 'MS': 'Centro-Oeste', 'GO': 'Centro-Oeste', 'DF': 'Centro-Oeste',
    'MG': 'Sudeste', 'ES': 'Sudeste', 'RJ': 'Sudeste', 'SP': 'Sudeste',
    'PR': 'Sul', 'SC': 'Sul', 'RS': 'Sul'
  };

  // Check if location has Brazilian state abbreviation
  for (const state of brazilianStates) {
    if (loc.includes(`, ${state}`) || loc.endsWith(state)) {
      const cityName = loc.split(',')[0].trim();
      return {
        countryType: "nacional",
        region: stateRegionMap[state as keyof typeof stateRegionMap] || "Outras RegiÃµes",
        city: cityName
      };
    }
  }

  // Check if it's a cruise
  if (loc.toLowerCase().includes("cruzeiro")) {
    return {
      countryType: "cruzeiro",
      region: "Outros Roteiros",
      city: "MÃºltiplas Cidades"
    };
  }

  // Check if it mentions Brasil explicitly
  if (loc.includes("Brasil") || loc.includes("Brazil")) {
    return {
      countryType: "nacional",
      region: "Outras RegiÃµes",
      city: loc.split(',')[0].trim()
    };
  }

  // Otherwise assume international
  const cityName = loc.split(',')[0].trim();
  return {
    countryType: "internacional",
    region: "Outros PaÃ­ses",
    city: cityName
  };
}

async function addHierarchicalFields() {
  try {
    console.log("ðŸ”„ Iniciando classificaÃ§Ã£o hierÃ¡rquica avanÃ§ada das atividades...");

    // First, ensure the hierarchical columns exist (MySQL compatibility)
    await ensureHierarchicalColumns();

    // Get all activities
    const allActivities = await db.select().from(activities);
    console.log(`ðŸ“‹ Encontradas ${allActivities.length} atividades para classificar`);

    let updatedCount = 0;
    let createdCount = 0;

    for (const activity of allActivities) {
      const mapping = locationMapping[activity.location as keyof typeof locationMapping];

      if (mapping) {
        // Use predefined mapping
        await db.update(activities)
          .set({
            countryType: mapping.countryType,
            region: mapping.region,
            city: mapping.city
          })
          .where(eq(activities.id, activity.id));

        console.log(`âœ… Classificada "${activity.title}" - ${mapping.countryType}/${mapping.region}/${mapping.city}`);
        updatedCount++;
      } else {
        // Use intelligent parsing for unmapped locations
        const parsedMapping = parseLocation(activity.location);

        await db.update(activities)
          .set({
            countryType: parsedMapping.countryType,
            region: parsedMapping.region,
            city: parsedMapping.city
          })
          .where(eq(activities.id, activity.id));

        console.log(`ðŸ” Auto-classificada "${activity.title}" - ${parsedMapping.countryType}/${parsedMapping.region}/${parsedMapping.city} (analisada)`);
        createdCount++;
      }
    }

    // Generate hierarchy statistics
    await generateHierarchyStatistics();

    console.log("âœ… ClassificaÃ§Ã£o hierÃ¡rquica concluÃ­da!");
    console.log(`ðŸ“Š EstatÃ­sticas: ${updatedCount} mapeadas, ${createdCount} auto-classificadas`);

  } catch (error) {
    console.error("âŒ Erro ao processar classificaÃ§Ã£o hierÃ¡rquica:", error);
    throw error;
  }
}

// Ensure hierarchical columns exist in the database
async function ensureHierarchicalColumns() {
  try {
    console.log("ðŸ”§ Verificando colunas hierÃ¡rquicas...");

    // Try to add columns if they don't exist (MySQL safe approach)
    const alterQueries = [
      `ALTER TABLE activities ADD COLUMN country_type VARCHAR(50) DEFAULT 'nacional' NOT NULL`,
      `ALTER TABLE activities ADD COLUMN region VARCHAR(100) DEFAULT '' NOT NULL`,
      `ALTER TABLE activities ADD COLUMN city VARCHAR(100) DEFAULT '' NOT NULL`
    ];

    for (const query of alterQueries) {
      try {
        await db.execute(sql.raw(query));
        console.log(`âœ… Coluna adicionada: ${query.split(' ADD COLUMN ')[1].split(' ')[0]}`);
      } catch (error: any) {
        if (error.code === 'ER_DUP_FIELDNAME') {
          // Column already exists, that's fine
          continue;
        } else {
          console.log(`â„¹ï¸ Coluna jÃ¡ existe: ${query.split(' ADD COLUMN ')[1].split(' ')[0]}`);
        }
      }
    }

  } catch (error) {
    console.log("â„¹ï¸ Colunas hierÃ¡rquicas jÃ¡ existem ou foram adicionadas");
  }
}

// Generate and display hierarchy statistics
async function generateHierarchyStatistics() {
  try {
    console.log("ðŸ“Š Gerando estatÃ­sticas hierÃ¡rquicas...");

    const stats = await db.select({
      countryType: activities.countryType,
      region: activities.region,
      count: sql<number>`count(*)`.as('count')
    })
    .from(activities)
    .where(eq(activities.isActive, true))
    .groupBy(activities.countryType, activities.region)
    .orderBy(activities.countryType, activities.region);

