    }

  } catch (error) {
    console.error("âŒ Erro na validaÃ§Ã£o de consistÃªncia:", error);
  }
}

// Main execution function
async function runHierarchicalUpdate() {
  try {
    console.log("ðŸš€ Iniciando atualizaÃ§Ã£o hierÃ¡rquica completa...");
    console.log(`â° ${new Date().toLocaleString('pt-BR')}`);

