server/add-rating-columns.ts.content:
import { db } from './db.js';
import { sql } from 'drizzle-orm';

async function addRatingColumns() {
  console.log('ðŸ”§ Adicionando colunas de rating necessÃ¡rias...');

  try {
    // Verificar se a coluna total_ratings jÃ¡ existe
    const columns = await db.execute(sql`DESCRIBE activities`);
    const hasRatingColumns = columns.some(col => col.Field === 'total_ratings');

    if (!hasRatingColumns) {
      // Adicionar colunas de rating se nÃ£o existirem
      await db.execute(sql`
        ALTER TABLE activities 
        ADD COLUMN total_ratings INT DEFAULT 0 NOT NULL,
        ADD COLUMN rating_sum DECIMAL(10,2) DEFAULT 0.00 NOT NULL
      `);
      console.log('âœ… Colunas de rating adicionadas!');
    } else {
      console.log('â„¹ï¸ Colunas de rating jÃ¡ existem');
    }

    // Verificar se a tabela activity_budget_proposals tem as colunas necessÃ¡rias
    const proposalColumns = await db.execute(sql`DESCRIBE activity_budget_proposals`);
    const hasProposalColumns = proposalColumns.some(col => col.Field === 'votes');

    if (!hasProposalColumns) {
      await db.execute(sql`
        ALTER TABLE activity_budget_proposals 
        ADD COLUMN votes INT DEFAULT 0 NOT NULL,
        ADD COLUMN is_popular BOOLEAN DEFAULT FALSE NOT NULL
      `);
      console.log('âœ… Colunas de propostas adicionadas!');
    }

  } catch (error) {
    console.error('âŒ Erro ao adicionar colunas:', error);
  }
}

// Executar se chamado diretamente
if (import.meta.url === `file://${process.argv[1]}`) {
  addRatingColumns()
    .then(() => {
      console.log('âœ… Processo concluÃ­do!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('âŒ Erro no processo:', error);
      process.exit(1);
    });
}

