import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import session from "express-session";
import bcrypt from "bcryptjs";
import type { Express } from "express";
import { storage, sessionConfig } from "./storage";
import type { User } from "@shared/schema";

declare global {
  namespace Express {
    interface User {
      id: number;
      username: string;
      email: string;
      full_name: string;
      phone: string;
      bio?: string;
      location?: string;
      profile_photo?: string;
      languages?: string[];
      interests?: string[];
      travel_styles?: string[];
      is_verified?: boolean;
      average_rating?: string;
      total_ratings?: number;
    }
  }
}

export function setupAuth(app: Express) {
  // ConfiguraÃ§Ã£o de sessÃ£o
  app.use(session({
    secret: process.env.SESSION_SECRET || "viaja-junto-secret-key-2024",
    resave: false,
    saveUninitialized: true, // Create session even if not modified
    cookie: {
      secure: false, // false para desenvolvimento
      httpOnly: false, // Allow frontend access in development
      maxAge: 24 * 60 * 60 * 1000, // 24 horas
      sameSite: 'lax', // Use lax for same-site in Replit
      path: '/',
      domain: undefined, // Use default domain
      partitioned: false // Prevent partitioned cookies
    },
    name: 'connect.sid',
    store: storage.sessionStore,
    rolling: true // Extend session on each request
  }));

  // Add CORS headers for development
  app.use((req, res, next) => {
    res.header('Access-Control-Allow-Origin', req.headers.origin || '*');
    res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    res.header('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept, Authorization');
    res.header('Access-Control-Allow-Credentials', 'true');

    // Handle preflight requests
    if (req.method === 'OPTIONS') {
      res.sendStatus(200);
      return;
    }

    next();
  });

  // Inicializar Passport ANTES do middleware customizado
  app.use(passport.initialize());
  app.use(passport.session());

  // Custom authentication middleware to handle both session and manual session ID
  app.use(async (req: any, res, next) => {
    // Log session information for debugging
    if (req.path.startsWith('/api/')) {
      console.log('ðŸ” Session debug:', {
        url: req.url,
        method: req.method,
        sessionID: req.sessionID,
        isAuthenticated: req.isAuthenticated ? req.isAuthenticated() : 'undefined',
        hasUser: !!req.user,
        sessionData: req.session,
        cookies: Object.keys(req.cookies || {}),
        headers: {
          cookie: req.headers.cookie ? 'presente' : 'ausente',
          sessionIdHeader: req.headers['x-session-id'] ? 'presente' : 'ausente'
        }
      });
    }

    // If already authenticated via session, continue
    if (req.isAuthenticated && req.isAuthenticated() && req.user) {
      return next();
    }

    // Check for session ID in headers (fallback for browser compatibility)
    const sessionId = req.headers['x-session-id'] || req.query.sessionId;
    if (sessionId && req.path.startsWith('/api/')) {
      try {
        // Try to find session in store
        const sessionData = await new Promise((resolve, reject) => {
          sessionConfig.store.get(sessionId, (err, session) => {
            if (err) reject(err);
            else resolve(session);
          });
        });

        if (sessionData && (sessionData as any).passport && (sessionData as any).passport.user) {
          const userId = (sessionData as any).passport.user;
          const user = await storage.getUserById(userId);

          if (user) {
            // Manually set user on request
            const { password: _, ...userWithoutPassword } = user;
            const cleanUser = {
              ...userWithoutPassword,
              bio: userWithoutPassword.bio || undefined,
              location: userWithoutPassword.location || undefined,
              profile_photo: userWithoutPassword.profile_photo || undefined,
              languages: userWithoutPassword.languages || undefined,
              interests: userWithoutPassword.interests || undefined,
              travel_styles: userWithoutPassword.travel_styles || undefined,
            };

            req.user = cleanUser as any;
            req.isAuthenticated = () => true;
            console.log('âœ… AutenticaÃ§Ã£o manual bem-sucedida:', user.username);
          }
        }
      } catch (error) {
        console.error('Erro na autenticaÃ§Ã£o manual:', error);
      }
    }

    // Final debug log after authentication attempts
    if (req.path.startsWith('/api/')) {
      console.log('ðŸ” Verificando usuÃ¡rio atual:', {
        isAuthenticated: req.isAuthenticated ? req.isAuthenticated() : false,
        hasUser: !!req.user,
        user: req.user ? req.user.username : undefined,
        sessionID: req.sessionID,
        session: req.session,
        cookies: req.cookies
      });
    }
    next();
  });

  // EstratÃ©gia local
  passport.use(new LocalStrategy(
    {
      usernameField: 'identifier',
      passwordField: 'password'
    },
    async (identifier, password, done) => {
      try {
        // Tenta buscar o usuÃ¡rio por username, email ou telefone
        let user = await storage.getUserByUsername(identifier);

        if (!user) {
          user = await storage.getUserByEmail(identifier);
        }

        if (!user) {
          // Remove formataÃ§Ã£o do telefone para busca
          const cleanPhone = identifier.replace(/\D/g, '');
          user = await storage.getUserByPhone(cleanPhone);
        }

        if (!user) {
          return done(null, false, { message: 'UsuÃ¡rio nÃ£o encontrado' });
        }

        // Check password using scrypt hash (same as used in database)
        console.log(`ðŸ”‘ Debug senha para ${user.username}:`);
        console.log(`  Senha fornecida: "${password}"`);
        console.log(`  Hash armazenado: "${user.password}"`);

        const { scryptSync } = await import('crypto');
        const [hash, salt] = user.password.split('.');
        console.log(`  Hash extraÃ­do: "${hash}"`);
        console.log(`  Salt extraÃ­do: "${salt}"`);

        const hashedPassword = scryptSync(password, salt, 64).toString('hex');
        console.log(`  Hash calculado: "${hashedPassword}"`);

        const isValid = hash === hashedPassword;
        console.log(`  âœ… Senhas coincidem: ${isValid}`);

        if (!isValid) {
          console.log(`âŒ Login falhado: Senha incorreta para ${user.username}`);
          return done(null, false, { message: 'Senha incorreta' });
        }

        // Remover senha do objeto usuÃ¡rio
        const { password: _, ...userWithoutPassword } = user;
        // Convert null values to undefined for TypeScript compatibility
        const cleanUser = {
          ...userWithoutPassword,
          bio: userWithoutPassword.bio || undefined,
          location: userWithoutPassword.location || undefined,
          profile_photo: userWithoutPassword.profile_photo || undefined,
          languages: userWithoutPassword.languages || undefined,
          interests: userWithoutPassword.interests || undefined,
          travel_styles: userWithoutPassword.travel_styles || undefined,
        };
        return done(null, cleanUser);
      } catch (error) {
        return done(error);
      }
    }
  ));

  // SerializaÃ§Ã£o do usuÃ¡rio
  passport.serializeUser((user, done) => {
    done(null, user.id);
  });

  // DeserializaÃ§Ã£o do usuÃ¡rio
  passport.deserializeUser(async (id: number, done) => {
    try {
      const user = await storage.getUserById(id);
      if (!user) {
        return done(null, false);
      }

      const { password: _, ...userWithoutPassword } = user;
      // Convert null values to undefined for TypeScript compatibility
      const cleanUser = {
        ...userWithoutPassword,
        bio: userWithoutPassword.bio || undefined,
        location: userWithoutPassword.location || undefined,
        profile_photo: userWithoutPassword.profile_photo || undefined,
        languages: userWithoutPassword.languages || undefined,
        interests: userWithoutPassword.interests || undefined,
        travel_styles: userWithoutPassword.travel_styles || undefined,
      };
      done(null, cleanUser);
    } catch (error) {
      done(error);
    }
  });

  // Rota de verificaÃ§Ã£o do usuÃ¡rio
  app.get("/api/user", async (req, res) => {
    if (req.isAuthenticated() && req.user) {
      // Always fetch fresh user data from database to ensure latest verification status
      try {
        const freshUser = await storage.getUserById((req.user as any).id);
        if (freshUser) {
          const { password: _, ...userWithoutPassword } = freshUser;
          const cleanUser = {
            ...userWithoutPassword,
            bio: userWithoutPassword.bio || undefined,
            location: userWithoutPassword.location || undefined,
            profile_photo: userWithoutPassword.profile_photo || undefined,
            languages: userWithoutPassword.languages || undefined,
            interests: userWithoutPassword.interests || undefined,
            travel_styles: userWithoutPassword.travel_styles || undefined,
          };
          console.log('âœ… Fresh user data loaded, is_verified:', cleanUser.is_verified);
          res.json(cleanUser);
        } else {
          res.status(401).json({ message: "UsuÃ¡rio nÃ£o encontrado" });
        }
      } catch (error) {
        console.error('Erro ao buscar usuÃ¡rio:', error);
        res.status(500).json({ message: "Erro interno do servidor" });
      }
    } else {
      console.log('ðŸ” Verificando usuÃ¡rio atual:', {
        isAuthenticated: req.isAuthenticated(),
        hasUser: !!req.user,
        user: req.user,
        sessionID: req.sessionID,
        session: req.session,
        cookies: req.cookies
      });
      res.status(401).json({ message: "NÃ£o autorizado" });
    }
  });

  // Debug route to test API functionality
  app.get("/api/auth/test", (req, res) => {
    res.json({ message: "API estÃ¡ funcionando", timestamp: new Date().toISOString() });
  });

  // Rota de login
  app.post("/api/auth/login", (req, res, next) => {
    passport.authenticate('local', (err: any, user: any, info: any) => {
      if (err) {
        console.error('Erro na autenticaÃ§Ã£o:', err);
        return res.status(500).json({ message: "Erro interno do servidor" });
      }

      if (!user) {
        console.log('Login falhado:', info?.message);
        return res.status(401).json({ 
          message: info?.message || "Credenciais invÃ¡lidas" 
        });
      }

      req.logIn(user, (err) => {
        if (err) {
          console.error('Erro ao fazer login:', err);
          return res.status(500).json({ message: "Erro ao fazer login" });
        }

        console.log('Login bem-sucedido:', {
          userId: user.id,
          username: user.username,
          sessionID: req.sessionID,
          isAuthenticated: req.isAuthenticated()
        });

        // Force session save to ensure persistence
        req.session.save((err) => {
          if (err) {
            console.error('Erro ao salvar sessÃ£o:', err);
            return res.status(500).json({ message: "Erro ao salvar sessÃ£o" });
          }

          console.log('SessÃ£o salva com sucesso. SessionID:', req.sessionID);
          console.log('Cookie headers:', res.getHeaders());

          // Also send session ID in response for browser compatibility
          return res.json({
            ...user,
            sessionId: req.sessionID
          });
        });
      });
    })(req, res, next);
  });

  // Rota de registro
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { username, password, email, fullName, phone, bio, location, languages, interests, travelStyles, referredBy } = req.body;

      // Verificar se usuÃ¡rio jÃ¡ existe
      const existingUser = await storage.getUserByUsername(username);
      if (existingUser) {
        return res.status(400).json({ message: "Nome de usuÃ¡rio jÃ¡ existe" });
      }

      const existingEmail = await storage.getUserByEmail(email);
      if (existingEmail) {
        return res.status(400).json({ message: "Email jÃ¡ estÃ¡ cadastrado" });
      }

      // Hash da senha using scrypt (consistent with test user)
      const { scrypt, randomBytes } = await import('crypto');
      const { promisify } = await import('util');
      const scryptAsync = promisify(scrypt);

      const salt = randomBytes(16).toString("hex");
      const buf = (await scryptAsync(password, salt, 64)) as Buffer;
      const hashedPassword = `${buf.toString("hex")}.${salt}`;

      // Determinar se usuÃ¡rio deve ser verificado automaticamente
      const isVerified = !!referredBy; // UsuÃ¡rios com cÃ³digo de indicaÃ§Ã£o sÃ£o verificados automaticamente
      const verificationMethod = referredBy ? 'referral' : null;

      // Criar usuÃ¡rio
      const newUser = await storage.createUser({
        username,
        password: hashedPassword,
        email,
        fullName,
        phone,
        bio,
        location,
        languages,
        interests,
        travelStyles,
        referredBy,
        isVerified,
        verificationMethod
      });

      // Fazer login automÃ¡tico
      const { password: _, ...userWithoutPassword } = newUser;
      // Convert null values to undefined for TypeScript compatibility
      const cleanUser = {
        ...userWithoutPassword,
        bio: userWithoutPassword.bio || undefined,
        location: userWithoutPassword.location || undefined,
        profilePhoto: userWithoutPassword.profilePhoto || undefined,
        languages: userWithoutPassword.languages || undefined,
        interests: userWithoutPassword.interests || undefined,
        travelStyles: userWithoutPassword.travelStyles || undefined,
      };

      req.logIn(cleanUser, (err) => {
        if (err) {
          return res.status(500).json({ message: "Erro ao fazer login automÃ¡tico" });
        }

        return res.status(201).json(cleanUser);
      });

    } catch (error) {
      console.error('Erro no registro:', error);
      res.status(500).json({ message: "Erro interno do servidor" });
    }
  });

  // Rota de logout
  app.post("/api/logout", (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ message: "Erro ao fazer logout" });
      }

      req.session.destroy((err) => {
        if (err) {
          return res.status(500).json({ message: "Erro ao destruir sessÃ£o" });
        }

        res.clearCookie('viaja.session');
        res.json({ message: "Logout realizado com sucesso" });
      });
    });
  });

  // Rota para solicitaÃ§Ãµes do usuÃ¡rio
  app.get("/api/user-requests", async (req, res) => {
    try {
      if (!req.isAuthenticated() || !req.user) {
        return res.status(401).json({ message: "NÃ£o autorizado" });
      }

      const requests = await storage.getUserTripRequests(req.user.id);
      res.json(requests);
    } catch (error) {
      console.error('Erro ao buscar solicitaÃ§Ãµes:', error);
      res.status(500).json({ message: "Erro ao buscar solicitaÃ§Ãµes" });
    }
  });
}

