        continue;
      }

      console.log(`âœ… Destino encontrado: ${destino.name} (ID: ${destino.id})`);

      for (const atividade of atividades) {
        try {
          // Inserir atividade
          const [atividadeInserida] = await db.insert(activities).values({
            title: atividade.title,
            description: atividade.description,
            destination_id: destino.id,
            category: atividade.category,
            difficulty_level: "easy",
            duration: atividade.duration,
            min_participants: 1,
            max_participants: 20,
            languages: ["PortuguÃªs", "InglÃªs"],
            inclusions: ["Acompanhamento profissional", "Seguro bÃ¡sico"],
            exclusions: ["AlimentaÃ§Ã£o", "Transporte pessoal"],
            requirements: ["Idade mÃ­nima: 12 anos"],
            cancellation_policy: "Cancelamento gratuito atÃ© 24h antes",
            contact_info: {
              email: "contato@partiutrip.com",
              phone: "+55 11 99999-9999",
              whatsapp: "+55 11 99999-9999"
            },
            cover_image: atividade.cover_image,
            images: atividade.images,
            created_by_id: 9, // Usando o usuÃ¡rio admin tom
            is_active: true,
            // Campos herdados do destino
            destination_name: destino.name,
            city: destino.name, // destinations nÃ£o tem campo city separado
            state: destino.state,
            country: destino.country,
            country_type: destino.country_type as "nacional" | "internacional",
            region: destino.region,
            continent: destino.continent
          }).returning();

          console.log(`  âœ… Atividade inserida: ${atividade.title} (ID: ${atividadeInserida.id})`);
          totalAtividades++;

          // Inserir propostas de orÃ§amento
          for (const proposta of atividade.propostas) {
            await db.insert(activityBudgetProposals).values({
              activity_id: atividadeInserida.id,
              created_by: 9,
              title: proposta.title,
              description: proposta.description,
              price_type: "per_person",
              amount: proposta.price.toString(),
              currency: "BRL",
              inclusions: proposta.inclusions,
              exclusions: ["Despesas pessoais", "Gorjetas"],
              is_active: true,
              votes: Math.floor(Math.random() * 50) + 10 // 10-60 votos aleatÃ³rios
            });
            totalPropostas++;
          }

          // Inserir reviews de exemplo
          for (const review of reviewsExemplo) {
            await db.insert(activityReviews).values({
              activity_id: atividadeInserida.id,
              user_id: 9, // UsuÃ¡rio tom
              rating: review.rating,
              review: review.review,
              photos: review.photos,
              visit_date: review.visit_date,
              helpful_votes: Math.floor(Math.random() * 15) + 1,
              is_verified: true
            });
            totalReviews++;
          }

          // Atualizar contadores da atividade usando eq
          await db.update(activities)
            .set({
              average_rating: "4.7",
              total_ratings: 3
            })
            .where(sql`id = ${atividadeInserida.id}`);

        } catch (error) {
          console.error(`âŒ Erro ao inserir atividade ${atividade.title}:`, error);
        }
      }
    }

    console.log(`\nðŸŽ‰ Cadastro concluÃ­do!`);
    console.log(`ðŸ“Š Resumo:`);
    console.log(`   â€¢ ${totalAtividades} atividades cadastradas`);
    console.log(`   â€¢ ${totalPropostas} propostas de orÃ§amento criadas`);
    console.log(`   â€¢ ${totalReviews} reviews adicionados`);

  } catch (error) {
    console.error("âŒ Erro durante o cadastro:", error);
  }
}

// Executar o script
cadastrarAtividades();

