        INSERT INTO activity_reviews (
          activity_id, user_id, rating, comment, visit_date, 
          is_verified, helpful_votes
        ) VALUES (
          ${activityId},
          ${user.id},
          ${rating},
          ${comment},
          ${visitDate.toISOString().split('T')[0]},
          ${Math.random() > 0.3}, -- 70% verificadas
          ${helpfulVotes}
        )
      `);
    } catch (error) {
      // Ignora duplicatas (mesmo usuÃ¡rio avaliando a mesma atividade)
      if (!error.message.includes('Duplicate entry')) {
        console.error(`Erro ao criar avaliaÃ§Ã£o: ${error.message}`);
      }
    }
  }
}

async function updateActivityRatings() {
  // Atualizar a mÃ©dia de rating para cada atividade
  await db.execute(sql`
    UPDATE activities a
    SET 
      average_rating = (
        SELECT COALESCE(AVG(r.rating), 0)
        FROM activity_reviews r 
        WHERE r.activity_id = a.id AND r.is_hidden = FALSE
      ),
      total_ratings = (
        SELECT COUNT(*)
        FROM activity_reviews r 
        WHERE r.activity_id = a.id AND r.is_hidden = FALSE
      )
  `);
}

// Executar se chamado diretamente
if (import.meta.url === `file://${process.argv[1]}`) {
  createRealisticSeedData()
    .then(() => {
      console.log('âœ… Seed realÃ­stico concluÃ­do!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('âŒ Erro no seed:', error);
      process.exit(1);
    });
}

