#!/usr/bin/env tsx
import { db } from './db.js';
import { activities, activityBudgetProposals } from '../shared/schema.js';
import { sql } from 'drizzle-orm';

/**
 * Create budget proposals for activities that don't have any
 */

async function createMissingProposals() {
  console.log('ðŸ”§ Creating missing budget proposals...');

  // Find activities without proposals
  const activitiesWithoutProposals = await db.select({
    id: activities.id,
    title: activities.title,
    location: activities.location,
    category: activities.category,
    countryType: activities.countryType
  }).from(activities)
  .leftJoin(activityBudgetProposals, sql`${activities.id} = ${activityBudgetProposals.activityId}`)
  .where(sql`${activityBudgetProposals.activityId} IS NULL`);

  console.log(`ðŸ“Š Found ${activitiesWithoutProposals.length} activities without proposals`);

  for (const activity of activitiesWithoutProposals) {
    console.log(`ðŸ’° Creating proposals for: ${activity.title}`);

    // Determine currency and price range based on location
    let currency = 'BRL';
    let basePrice = 100;

    if (activity.countryType === 'internacional') {
      if (activity.location?.includes('Argentina')) {
        currency = 'USD';
        basePrice = 15;
      } else if (activity.location?.includes('EUA') || activity.location?.includes('Nova York')) {
        currency = 'USD';
        basePrice = 50;
      } else if (activity.location?.includes('Reino Unido') || activity.location?.includes('Londres')) {
        currency = 'GBP';
        basePrice = 25;
      } else if (activity.location?.includes('ItÃ¡lia') || activity.location?.includes('Roma')) {
        currency = 'EUR';
        basePrice = 30;
      } else if (activity.location?.includes('FranÃ§a') || activity.location?.includes('Paris')) {
        currency = 'EUR';
        basePrice = 35;
      }
    }

    // Adjust price based on category
    if (activity.category === 'adventure' || activity.category === 'water_sports') {
      basePrice *= 1.5;
    } else if (activity.category === 'cultural' || activity.category === 'sightseeing') {
      basePrice *= 0.8;
    } else if (activity.category === 'food_tours') {
      basePrice *= 1.2;
    }

    const proposals = [
      {
        title: 'EconÃ´mico',
        description: 'OpÃ§Ã£o bÃ¡sica com o essencial incluÃ­do',
        amount: basePrice.toFixed(2),
        currency,
        inclusions: JSON.stringify(['Guia local', 'Entrada bÃ¡sica', 'Seguro']),
        exclusions: JSON.stringify(['AlimentaÃ§Ã£o', 'Transporte', 'Souvenirs']),
        activityId: activity.id,
        createdBy: 1,
        isActive: true
      },
      {
        title: 'Completo',
        description: 'ExperiÃªncia completa com extras incluÃ­dos',
        amount: (basePrice * 1.5).toFixed(2),
        currency,
        inclusions: JSON.stringify(['Guia especializado', 'Entrada premium', 'Lanche', 'Fotos', 'Seguro']),
        exclusions: JSON.stringify(['Bebidas extras', 'Transporte', 'Souvenirs']),
        activityId: activity.id,
        createdBy: 1,
        isActive: true
      },
      {
        title: 'Premium',
        description: 'ExperiÃªncia VIP com tudo incluÃ­do',
        amount: (basePrice * 2.5).toFixed(2),
        currency,
        inclusions: JSON.stringify(['Guia privado', 'Acesso VIP', 'RefeiÃ§Ã£o completa', 'Transporte', 'Fotos profissionais', 'Brinde exclusivo']),
        exclusions: JSON.stringify(['Bebidas alcoÃ³licas premium', 'Gorjetas']),
        activityId: activity.id,
        createdBy: 1,
        isActive: true
      }
    ];

    for (const proposal of proposals) {
      try {
        await db.insert(activityBudgetProposals).values(proposal);
        console.log(`  âœ… ${proposal.title}: ${proposal.currency} ${proposal.amount}`);
      } catch (error: any) {
        if (error.code !== 'ER_DUP_ENTRY') {
          console.error(`  âŒ Error creating ${proposal.title}:`, error.message);
        }
      }
    }
  }

  console.log('ðŸŽ‰ Missing proposals creation completed!');
}

