import { db } from './db.js';
import { sql } from 'drizzle-orm';

async function createActivityReviews() {
  console.log('â­ Criando avaliaÃ§Ãµes realÃ­sticas para todas as atividades...');

  try {
    // Obter todas as atividades e usuÃ¡rios
    const activities = await db.execute(sql`SELECT id, title FROM activities`);
    const users = await db.execute(sql`SELECT id, username FROM users`);

    console.log(`ðŸ“Š Encontradas ${activities.length} atividades e ${users.length} usuÃ¡rios`);

    // Limpar avaliaÃ§Ãµes existentes (se houver)
    await db.execute(sql`DELETE FROM activity_reviews`);

    let totalReviews = 0;

    // ComentÃ¡rios realÃ­sticos para as avaliaÃ§Ãµes
    const reviewComments = [
      'ExperiÃªncia incrÃ­vel! Superou todas as expectativas. Recomendo muito!',
      'Muito bom, mas poderia ser um pouco melhor organizado.',
      'Perfeito! Vista sensacional e atendimento excelente.',
      'Legal, mas achei um pouco caro para o que oferece.',
      'FantÃ¡stico! Uma das melhores experiÃªncias que jÃ¡ tive.',
      'Boa atividade, mas estava muito cheio no dia.',
      'Maravilhoso! Vale cada centavo. Voltaria com certeza.',
      'Interessante, mas esperava mais. Talvez seja melhor em outra Ã©poca.',
      'Simplesmente espetacular! NÃ£o hÃ¡ palavras para descrever.',
      'Gostei, mas o tempo da atividade foi um pouco corrido.',
      'IncrÃ­vel! A vista Ã© de tirar o fÃ´lego. Recomendo ir no pÃ´r do sol.',
      'Muito bem organizado e o guia foi excelente. Valeu muito a pena!',
      'Atividade boa, mas o preÃ§o estÃ¡ meio salgado para o que oferece.',
      'Adorei! Foi uma das melhores partes da minha viagem.',
      'ExperiÃªncia Ãºnica! Definitivamente algo que todos deveriam fazer.'
    ];

    for (const activity of activities) {
      console.log(`ðŸ’¬ Criando avaliaÃ§Ãµes para: ${activity.title}`);

      // Criar entre 2 a 6 avaliaÃ§Ãµes por atividade
      const numReviews = Math.floor(Math.random() * 5) + 2;
      const shuffledUsers = [...users].sort(() => Math.random() - 0.5);

      for (let i = 0; i < Math.min(numReviews, shuffledUsers.length); i++) {
        const user = shuffledUsers[i];
        const rating = Math.floor(Math.random() * 2) + 4; // Rating entre 4 e 5 (mais realÃ­stico)
        const comment = reviewComments[Math.floor(Math.random() * reviewComments.length)];
        const helpfulVotes = Math.floor(Math.random() * 25) + 1;
        const isVerified = Math.random() > 0.25; // 75% verificadas

        // Data de visita aleatÃ³ria nos Ãºltimos 6 meses
        const visitDate = new Date();
        visitDate.setDate(visitDate.getDate() - Math.floor(Math.random() * 180));
        const visitDateStr = visitDate.toISOString().split('T')[0];

        try {
          await db.execute(sql`
            INSERT INTO activity_reviews (
              activity_id, user_id, rating, comment, visit_date, 
              is_verified, helpful_votes, is_hidden
            ) VALUES (
              ${activity.id},
              ${user.id},
              ${rating},
              ${comment},
              ${visitDateStr},
              ${isVerified},
              ${helpfulVotes},
              false
            )
          `);
          totalReviews++;
        } catch (error) {
          // Ignora erro de usuÃ¡rio duplicado avaliando a mesma atividade
          if (!error.message.includes('Duplicate entry')) {
            console.error(`Erro ao criar avaliaÃ§Ã£o: ${error.message}`);
          }
        }
      }
    }

    console.log(`âœ… ${totalReviews} avaliaÃ§Ãµes criadas com sucesso!`);

    // Atualizar as mÃ©dias de rating das atividades
    console.log('ðŸ“Š Atualizando mÃ©dias de rating...');
    await updateActivityRatings();

  } catch (error) {
    console.error('âŒ Erro ao criar avaliaÃ§Ãµes:', error);
    throw error;
  }
}

async function updateActivityRatings() {
  // Atualizar a mÃ©dia de rating e total de avaliaÃ§Ãµes para cada atividade
  await db.execute(sql`
    UPDATE activities a
    SET 
      average_rating = (
        SELECT COALESCE(AVG(r.rating), 0)
        FROM activity_reviews r 
        WHERE r.activity_id = a.id AND r.is_hidden = FALSE
      ),
      total_ratings = (
        SELECT COUNT(*)
        FROM activity_reviews r 
        WHERE r.activity_id = a.id AND r.is_hidden = FALSE
      )
  `);

  console.log('âœ… MÃ©dias de rating atualizadas!');
}

// Executar se chamado diretamente
if (import.meta.url === `file://${process.argv[1]}`) {
  createActivityReviews()
    .then(() => {
      console.log('âœ… CriaÃ§Ã£o de avaliaÃ§Ãµes concluÃ­da!');
      process.exit(0);
    })
    .catch((error) => {
      console.error('âŒ Erro no processo:', error);
      process.exit(1);
    });
}

