import { drizzle } from "drizzle-orm/postgres-js";
import postgres from "postgres";
import * as schema from "@shared/schema";
import { sql } from "drizzle-orm";
import { users } from "@shared/schema";
import dotenv from "dotenv";
import { initializeDatabaseSecurity, validateDatabaseUrl as validateDbUrl } from "./db-security";

// Carregar variÃ¡veis de ambiente
dotenv.config();

// INICIALIZAR PROTEÃ‡Ã•ES DE SEGURANÃ‡A
initializeDatabaseSecurity();

console.log(`ðŸ”— Conectando ao PostgreSQL...`);

// PROTEÃ‡ÃƒO DE SEGURANÃ‡A: ValidaÃ§Ã£o rigorosa da DATABASE_URL
function validateDatabaseUrl(url: string): string {
  if (!url) {
    throw new Error("ðŸš¨ SEGURANÃ‡A: DATABASE_URL Ã© obrigatÃ³ria e nÃ£o pode estar vazia");
  }

  // Lista de domÃ­nios autorizados para conexÃ£o
  const allowedDomains = [
    'neon.tech',           // Neon PostgreSQL (configurado no .env)
    'ep-dark-frog-acs6c8yp.sa-east-1.aws.neon.tech'  // DomÃ­nio especÃ­fico do projeto
  ];

  // Bloquear tentativas de conexÃ£o com bancos nativos do Replit
  const blockedPatterns = [
    'replit.dev',
    'repl.co',
    'localhost',
    '127.0.0.1',
    '0.0.0.0',
    'sqlite',
    'file:',
    'memory:'
  ];

  // Verificar se contÃ©m padrÃµes bloqueados
  for (const pattern of blockedPatterns) {
    if (url.toLowerCase().includes(pattern.toLowerCase())) {
      throw new Error(`ðŸš¨ SEGURANÃ‡A: ConexÃ£o bloqueada - tentativa de usar banco nÃ£o autorizado: ${pattern}`);
    }
  }

  // Verificar se estÃ¡ usando um domÃ­nio autorizado
  const urlObj = new URL(url);
  const isAuthorized = allowedDomains.some(domain => 
    urlObj.hostname.includes(domain)
  );

  if (!isAuthorized) {
    throw new Error(`ðŸš¨ SEGURANÃ‡A: DomÃ­nio nÃ£o autorizado: ${urlObj.hostname}. Apenas domÃ­nios Neon sÃ£o permitidos.`);
  }

  // Verificar se Ã© PostgreSQL
  if (!url.startsWith('postgresql://') && !url.startsWith('postgres://')) {
    throw new Error("ðŸš¨ SEGURANÃ‡A: Apenas conexÃµes PostgreSQL sÃ£o permitidas");
  }

  console.log(`âœ… SEGURANÃ‡A: URL do banco validada - conectando ao Neon PostgreSQL`);
  return url;
}

// ConfiguraÃ§Ã£o do banco PostgreSQL com validaÃ§Ãµes de seguranÃ§a
if (!process.env.DATABASE_URL) {
  throw new Error("ðŸš¨ SEGURANÃ‡A: DATABASE_URL Ã© obrigatÃ³ria no arquivo .env");
}

// Validar e proteger a URL do banco
const secureDbUrl = validateDatabaseUrl(process.env.DATABASE_URL);
const connection = postgres(secureDbUrl, { 
  ssl: 'require',
  // ConfiguraÃ§Ãµes adicionais de seguranÃ§a
  connect_timeout: 10,
  max: 10,
  idle_timeout: 20,
  max_lifetime: 60 * 30 // 30 minutos
});

// Configurar Drizzle com PostgreSQL
export const db = drizzle(connection, { schema });

// Testar conexÃ£o com validaÃ§Ãµes de seguranÃ§a
export async function testConnection() {
  try {
    console.log("ðŸ”— Testando conexÃ£o PostgreSQL...");

    // Testar conexÃ£o bÃ¡sica
    await db.execute(sql`SELECT 1`);

    // Verificar se estamos conectados ao banco correto
    const result = await db.execute(sql`SELECT current_database(), current_user, inet_server_addr()`);
    const dbInfo = result[0] as any;

    console.log("âœ… ConexÃ£o PostgreSQL estabelecida com sucesso!");
    console.log(`ðŸ“Š Banco: ${dbInfo.current_database}, UsuÃ¡rio: ${dbInfo.current_user}`);

    // VerificaÃ§Ã£o adicional de seguranÃ§a - confirmar que nÃ£o Ã© banco local
    const serverAddr = dbInfo.inet_server_addr;
    if (serverAddr && typeof serverAddr === 'string' && (
      serverAddr.includes('127.0.0.1') || 
      serverAddr.includes('localhost')
    )) {
      throw new Error("ðŸš¨ SEGURANÃ‡A: Detectada conexÃ£o com banco local - bloqueando");
    }

    return true;
  } catch (error) {
    console.error("âŒ Erro ao conectar com PostgreSQL:", error);
    return false;
  }
}

// Reset completo do banco
export async function resetDatabase() {
  const { resetDatabase: resetDB } = await import("./reset-database");
  return await resetDB();
}

// Inicializar tabelas usando Drizzle
export async function initializeTables() {
  try {
    console.log("ðŸ—ï¸ Inicializando tabelas do schema PostgreSQL...");

    // With Drizzle and PostgreSQL Neon, schema is managed by migrations
    // Check if we need to create initial test data
    try {
      const userCount = await db.select().from(users).limit(1);
      if (userCount.length === 0) {
        console.log("ðŸŒ± Criando dados bÃ¡sicos de teste...");
        const { createSimpleSeedData } = await import("./simple-postgres-seed");
        await createSimpleSeedData();
      }
    } catch (error) {
      console.log("ðŸŒ± Tabelas nÃ£o existem ainda, criando dados bÃ¡sicos...");
      const { createSimpleSeedData } = await import("./simple-postgres-seed");
      await createSimpleSeedData();
    }

    console.log("âœ… InicializaÃ§Ã£o das tabelas concluÃ­da!");
  } catch (error) {
    console.error("âŒ Erro na inicializaÃ§Ã£o do banco:", error);
    throw error;
  }
}

