#!/usr/bin/env tsx

import { db } from "./db";
import { sql } from "drizzle-orm";

async function fixDestinationReferences() {
  console.log("ðŸš€ Corrigindo referÃªncias de destination_id...");

  try {
    // 1. Verificar qual destination ID usar (Rio de Janeiro)
    const rioCheck = await db.execute(sql`SELECT id FROM destinations WHERE name = 'Rio de Janeiro' LIMIT 1`);

    if (rioCheck.length === 0) {
      console.log("âŒ Destino Rio de Janeiro nÃ£o encontrado. Criando...");
      await db.execute(sql`
        INSERT INTO destinations (name, state, country, country_type, continent, region, is_active, created_at)
        VALUES ('Rio de Janeiro', 'RJ', 'Brasil', 'nacional', 'AmÃ©rica do Sul', 'Sudeste', true, NOW())
      `);
    }

    const rioDestination = await db.execute(sql`SELECT id FROM destinations WHERE name = 'Rio de Janeiro' LIMIT 1`);
    const rioId = (rioDestination[0] as any).id;
    console.log(`ðŸ“ Usando Rio de Janeiro como destino padrÃ£o (ID: ${rioId})`);

    // 2. Corrigir trips Ã³rfÃ£os
    console.log("ðŸ” Verificando trips Ã³rfÃ£os...");
    const orphanTripsResult = await db.execute(sql`
      SELECT t.id, t.title, t.destination_id 
      FROM trips t 
      LEFT JOIN destinations d ON t.destination_id = d.id 
      WHERE d.id IS NULL
    `);

    console.log(`ðŸ“‹ Encontrados ${orphanTripsResult.length} trips Ã³rfÃ£os`);

    if (orphanTripsResult.length > 0) {
      await db.execute(sql`
        UPDATE trips 
        SET destination_id = ${rioId}
        WHERE id IN (
          SELECT trip_id FROM (
            SELECT t.id as trip_id
            FROM trips t 
            LEFT JOIN destinations d ON t.destination_id = d.id 
            WHERE d.id IS NULL
          ) as orphan_trips
        )
      `);
      console.log(`âœ… ${orphanTripsResult.length} trips Ã³rfÃ£os corrigidos`);
    }

    // 3. Corrigir activities Ã³rfÃ£os
    console.log("ðŸŽ¯ Verificando activities Ã³rfÃ£os...");
    const orphanActivitiesResult = await db.execute(sql`
      SELECT a.id, a.title, a.destination_id 
      FROM activities a 
      LEFT JOIN destinations d ON a.destination_id = d.id 
      WHERE d.id IS NULL
    `);

    console.log(`ðŸ“‹ Encontrados ${orphanActivitiesResult.length} activities Ã³rfÃ£os`);

    if (orphanActivitiesResult.length > 0) {
      await db.execute(sql`
        UPDATE activities 
        SET destination_id = ${rioId}
        WHERE id IN (
          SELECT activity_id FROM (
            SELECT a.id as activity_id
            FROM activities a 
            LEFT JOIN destinations d ON a.destination_id = d.id 
            WHERE d.id IS NULL
          ) as orphan_activities
        )
      `);
      console.log(`âœ… ${orphanActivitiesResult.length} activities Ã³rfÃ£os corrigidos`);
    }

    // 4. Agora tentar adicionar as foreign keys
    console.log("ðŸ”— Adicionando foreign keys...");

    try {
      await db.execute(sql`ALTER TABLE trips ADD CONSTRAINT trips_destinations_fk FOREIGN KEY (destination_id) REFERENCES destinations(id)`);
      console.log("âœ… Foreign key trips_destinations_fk adicionada");
    } catch (error: any) {
      if (error.message.includes('already exists')) {
        console.log("â„¹ï¸ Foreign key trips_destinations_fk jÃ¡ existe");
      } else {
        console.log("âš ï¸ Erro ao adicionar FK trips:", error.message);
      }
    }

    try {
      await db.execute(sql`ALTER TABLE activities ADD CONSTRAINT activities_destinations_fk FOREIGN KEY (destination_id) REFERENCES destinations(id)`);
      console.log("âœ… Foreign key activities_destinations_fk adicionada");
    } catch (error: any) {
      if (error.message.includes('already exists')) {
        console.log("â„¹ï¸ Foreign key activities_destinations_fk jÃ¡ existe");
      } else {
        console.log("âš ï¸ Erro ao adicionar FK activities:", error.message);
      }
    }

    // 5. VerificaÃ§Ã£o final
    console.log("ðŸ” VerificaÃ§Ã£o final...");

    const finalOrphanTrips = await db.execute(sql`
      SELECT COUNT(*) as count
      FROM trips t 
      LEFT JOIN destinations d ON t.destination_id = d.id 
      WHERE d.id IS NULL
    `);

    const finalOrphanActivities = await db.execute(sql`
      SELECT COUNT(*) as count
      FROM activities a 
      LEFT JOIN destinations d ON a.destination_id = d.id 
      WHERE d.id IS NULL
    `);

    console.log(`ðŸ“Š Trips Ã³rfÃ£os restantes: ${(finalOrphanTrips[0] as any).count}`);
    console.log(`ðŸ“Š Activities Ã³rfÃ£os restantes: ${(finalOrphanActivities[0] as any).count}`);

    console.log("ðŸŽ‰ MigraÃ§Ã£o de cities â†’ destinations concluÃ­da com sucesso!");

  } catch (error) {
    console.error("âŒ Erro durante a correÃ§Ã£o:", error);
    throw error;
  }
}

// Executar se chamado diretamente
fixDestinationReferences()
  .then(() => {
    console.log("âœ… CorreÃ§Ã£o executada com sucesso!");
    process.exit(0);
  })
  .catch((error) => {
    console.error("âŒ Falha na correÃ§Ã£o:", error);
    process.exit(1);
  });

export { fixDestinationReferences };

