          FOREIGN KEY (created_by) REFERENCES users(id) ON DELETE CASCADE
        )
      `);

      console.log('âœ… Tabela activity_budget_proposals criada!');
    } else {
      console.log('âœ… Tabela activity_budget_proposals existe');
    }

    // Check columns
    const [columns] = await connection.execute(`
      SELECT COLUMN_NAME, DATA_TYPE, IS_NULLABLE, COLUMN_KEY 
      FROM information_schema.COLUMNS 
      WHERE TABLE_SCHEMA = 'u715665838_partiutrip' 
      AND TABLE_NAME = 'activity_budget_proposals'
      ORDER BY ORDINAL_POSITION
    `);

    console.log('ðŸ“‹ Colunas da tabela:');
    columns.forEach((col: any) => {
