#!/usr/bin/env tsx

import { db } from "./db";
import { sql } from "drizzle-orm";

async function migrateCitiesToDestinations() {
  console.log("ðŸš€ Iniciando migraÃ§Ã£o de cities para destinations...");

  try {
    // 1. Renomear tabela cities para destinations
    console.log("ðŸ“‹ Renomeando tabela cities para destinations...");
    await db.execute(sql`RENAME TABLE cities TO destinations`);
    console.log("âœ… Tabela renomeada com sucesso");

    // 2. Atualizar foreign keys na tabela trips
    console.log("ðŸ”— Atualizando foreign keys na tabela trips...");

    // Primeiro remover a constraint existente
    try {
      await db.execute(sql`ALTER TABLE trips DROP FOREIGN KEY trips_ibfk_2`);
      console.log("âœ… Foreign key antiga removida");
    } catch (error: any) {
      console.log("â„¹ï¸ Foreign key trips_ibfk_2 nÃ£o encontrada, continuando...");
    }

    // Renomear coluna city_id para destination_id
    await db.execute(sql`ALTER TABLE trips CHANGE COLUMN city_id destination_id INT NOT NULL`);
    console.log("âœ… Coluna city_id renomeada para destination_id");

    // Adicionar nova foreign key
    await db.execute(sql`ALTER TABLE trips ADD CONSTRAINT trips_destinations_fk FOREIGN KEY (destination_id) REFERENCES destinations(id)`);
    console.log("âœ… Nova foreign key adicionada");

    // 3. Atualizar foreign keys na tabela activities
    console.log("ðŸŽ¯ Atualizando foreign keys na tabela activities...");

    // Verificar se a coluna city_id existe
    try {
      await db.execute(sql`ALTER TABLE activities CHANGE COLUMN city_id destination_id INT NOT NULL`);
      console.log("âœ… Coluna city_id em activities renomeada para destination_id");

      // Adicionar foreign key se nÃ£o existir
      await db.execute(sql`ALTER TABLE activities ADD CONSTRAINT activities_destinations_fk FOREIGN KEY (destination_id) REFERENCES destinations(id)`);
      console.log("âœ… Foreign key adicionada em activities");
    } catch (error: any) {
      console.log("â„¹ï¸ Coluna city_id em activities nÃ£o encontrada ou jÃ¡ migrada:", error.message);
    }

    // 4. Atualizar tabela destination_ratings se existir
    console.log("â­ Atualizando tabela de ratings...");
    try {
      await db.execute(sql`RENAME TABLE city_ratings TO destination_ratings`);
      console.log("âœ… Tabela city_ratings renomeada para destination_ratings");

      await db.execute(sql`ALTER TABLE destination_ratings CHANGE COLUMN city_id destination_id INT NOT NULL`);
      console.log("âœ… Coluna em destination_ratings atualizada");
    } catch (error) {
      console.log("â„¹ï¸ Tabela city_ratings nÃ£o encontrada:", error.message);
    }

    console.log("ðŸŽ‰ MigraÃ§Ã£o concluÃ­da com sucesso!");
    console.log("ðŸ“ Resumo das mudanÃ§as:");
    console.log("   - cities â†’ destinations");
    console.log("   - city_id â†’ destination_id em todas as tabelas");
    console.log("   - Foreign keys atualizadas");

  } catch (error) {
    console.error("âŒ Erro durante a migraÃ§Ã£o:", error);
    throw error;
  }
}

// Executar se chamado diretamente
migrateCitiesToDestinations()
  .then(() => {
    console.log("âœ… MigraÃ§Ã£o executada com sucesso!");
    process.exit(0);
  })
  .catch((error) => {
    console.error("âŒ Falha na migraÃ§Ã£o:", error);
    process.exit(1);
  });

export { migrateCitiesToDestinations };

