export async function migrateToCitiesTable() {
  try {
    console.log("ðŸ—ï¸ Criando tabela destinations...");

    // Create destinations table
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS destinations (
        id INT AUTO_INCREMENT PRIMARY KEY,
        name VARCHAR(255) NOT NULL,
        state VARCHAR(255),
        country VARCHAR(255) NOT NULL,
        country_type VARCHAR(50) NOT NULL,
        region VARCHAR(255),
        latitude DECIMAL(10,8),
        longitude DECIMAL(11,8), 
        timezone VARCHAR(50),
        is_active BOOLEAN DEFAULT TRUE NOT NULL,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
        UNIQUE KEY unique_city (name, state, country)
      )
    `);

    console.log("âœ… Tabela destinations criada!");

    // Insert all unique destinations
    console.log("ðŸ“ Inserindo cidades...");

    const cityInserts = Object.values(cityMappings).reduce((acc: LocationData[], city) => {
      const existing = acc.find(c => c.name === city.name && c.country === city.country);
      if (!existing) {
        acc.push(city);
      }
      return acc;
    }, []);

    for (const cityData of cityInserts) {
      try {
        await db.insert(destinations).values(cityData).ignore();
        console.log(`   âœ… Cidade inserida: ${cityData.name}, ${cityData.country}`);
      } catch (error) {
        console.log(`   âš ï¸ Cidade jÃ¡ existe: ${cityData.name}, ${cityData.country}`);
      }
    }

    console.log("ðŸ”„ Migrando dados das viagens...");

    // Add cityId column to trips table if not exists
    try {
      await db.execute(sql`
        ALTER TABLE trips 
        ADD COLUMN destination_id INT,
        ADD FOREIGN KEY (destination_id) REFERENCES destinations(id)
      `);
      console.log("   âœ… Coluna destination_id adicionada Ã  tabela trips");
    } catch (error) {
      console.log("   âš ï¸ Coluna destination_id jÃ¡ existe em trips");
    }

    // Get all trips with their current localidade (use raw SQL to avoid schema conflicts)
    const allTripsResult = await db.execute(sql`
      SELECT id, title, localidade FROM trips
    `);
    const allTrips = allTripsResult as Array<{id: number, title: string, localidade: string}>;

    // Update trips with cityId
    for (const trip of allTrips) {
      const cityMapping = cityMappings[trip.localidade as string];
      if (cityMapping) {
        // Find the city ID
        const [city] = await db.select().from(destinations).where(
          sql`name = ${cityMapping.name} AND country = ${cityMapping.country}`
        );

        if (city) {
          await db.execute(sql`
            UPDATE trips SET destination_id = ${city.id} WHERE id = ${trip.id}
          `);
          console.log(`   âœ… Viagem "${trip.title}" vinculada Ã  cidade ${cityMapping.name}`);
        }
      } else {
        console.log(`   âš ï¸ Mapeamento nÃ£o encontrado para: ${trip.localidade}`);
      }
    }

    console.log("ðŸŽ¯ Migrando dados das atividades...");

    // Add cityId column to activities table if not exists
    try {
      await db.execute(sql`
        ALTER TABLE activities 
        ADD COLUMN destination_id INT,
        ADD FOREIGN KEY (destination_id) REFERENCES destinations(id)
      `);
      console.log("   âœ… Coluna destination_id adicionada Ã  tabela activities");
    } catch (error) {
      console.log("   âš ï¸ Coluna destination_id jÃ¡ existe em activities");
    }

    // Get all activities using raw SQL to avoid schema conflicts
    const allActivitiesResult = await db.execute(sql`
      SELECT id, title, location, city FROM activities
    `);
    const allActivities = allActivitiesResult as Array<{id: number, title: string, location?: string, city?: string}>;

    // Update activities with cityId based on location field
    for (const activity of allActivities) {
      const activityLocation = activity.location || activity.city;

      // Skip if no location data
      if (!activityLocation) {
        console.log(`   âš ï¸ Atividade sem localizaÃ§Ã£o: ${activity.title}`);
        continue;
      }

      // Try to find matching city mapping
      let cityMapping: LocationData | undefined;

      // Direct mapping lookup
      cityMapping = cityMappings[activityLocation];

      // If not found, try partial matching
      if (!cityMapping) {
        const mappingKey = Object.keys(cityMappings).find(key => 
          key.includes(activityLocation) || activityLocation.includes(key.split(',')[0])
        );
        if (mappingKey) {
          cityMapping = cityMappings[mappingKey];
        }
      }

      if (cityMapping) {
        // Find the city ID
        const [city] = await db.select().from(destinations).where(
          sql`name = ${cityMapping.name} AND country = ${cityMapping.country}`
        );

        if (city) {
          await db.execute(sql`
            UPDATE activities SET destination_id = ${city.id} WHERE id = ${activity.id}
          `);
          console.log(`   âœ… Atividade "${activity.title}" vinculada Ã  cidade ${cityMapping.name}`);
        }
      } else {
        console.log(`   âš ï¸ Mapeamento nÃ£o encontrado para atividade: ${activityLocation}`);
      }
    }

    console.log("âœ… MigraÃ§Ã£o para tabela destinations concluÃ­da com sucesso!");

    // Show summary
    const cityCount = await db.select({ count: sql`count(*)` }).from(destinations);
    console.log(`ðŸ“Š Total de cidades cadastradas: ${cityCount[0].count}`);

  } catch (error) {
    console.error("âŒ Erro na migraÃ§Ã£o:", error);
    throw error;
  }
}

// Execute migration if run directly
if (import.meta.url === `file://${process.argv[1]}`) {
  migrateToCitiesTable()
    .then(() => {
