server/migrate-travel-styles.ts.content:
import { db } from './db';
import { users } from '../shared/schema';
import { sql } from 'drizzle-orm';

async function migrateTravelStyles() {
  console.log('ðŸ”„ Iniciando migraÃ§Ã£o de travel_style para travel_styles...');

  try {
    // Migrar dados existentes do travel_style para travel_styles
    const allUsers = await db.execute(sql`SELECT id, travel_style FROM users WHERE travel_style IS NOT NULL AND travel_styles IS NULL`);

    for (const user of allUsers) {
      const travelStyle = user.travel_style;
      if (travelStyle) {
        await db.execute(sql`UPDATE users SET travel_styles = JSON_ARRAY(${travelStyle}) WHERE id = ${user.id}`);
        console.log(`âœ… UsuÃ¡rio ${user.id} migrado: ${travelStyle} -> [${travelStyle}]`);
      }
    }

    // Remover coluna antiga
    await db.execute(sql`ALTER TABLE users DROP COLUMN travel_style`);
    console.log('âœ… Coluna travel_style removida com sucesso');

    console.log('ðŸŽ‰ MigraÃ§Ã£o concluÃ­da com sucesso!');
  } catch (error) {
    console.error('âŒ Erro durante a migraÃ§Ã£o:', error);

    // Se a coluna nÃ£o existe, apenas continue
    if (error.code === 'ER_CANT_DROP_FIELD_OR_KEY') {
      console.log('â„¹ï¸ Coluna travel_style jÃ¡ foi removida, continuando...');
    } else {
      throw error;
    }
  }
}

// Executar migraÃ§Ã£o se chamado diretamente
if (import.meta.url === `file://${process.argv[1]}`) {
  migrateTravelStyles().catch(console.error);
}

export { migrateTravelStyles };

