async function dropAllTables(connection: any) {
  console.log("ðŸ—‘ï¸ Removendo todas as tabelas...");

  const tables = [
    'expense_splits', 'expenses', 'user_ratings', 'activity_reviews', 
    'activity_budget_proposals', 'trip_requests', 'messages', 
    'trip_participants', 'trips', 'activities', 'referral_codes', 'users'
  ];

  for (const table of tables) {
    try {
      await connection.execute(`DROP TABLE IF EXISTS ${table}`);
      console.log(`âœ… Tabela ${table} removida`);
    } catch (error) {
      console.log(`âš ï¸ Erro ao remover ${table}:`, error);
    }
  }
}

async function createTables(connection: any) {
  console.log("ðŸ—ï¸ Criando todas as tabelas...");

  // Criar tabela de usuÃ¡rios
  await connection.execute(`
    CREATE TABLE users (
      id INT AUTO_INCREMENT PRIMARY KEY,
      username VARCHAR(255) NOT NULL UNIQUE,
      password TEXT NOT NULL,
      email VARCHAR(255) NOT NULL UNIQUE,
      full_name VARCHAR(255) NOT NULL,
      phone VARCHAR(20) NOT NULL,
      bio TEXT,
      location VARCHAR(255),
      profile_photo TEXT,
      languages JSON,
      interests JSON,
      travel_styles JSON,
      referred_by VARCHAR(50),
      is_verified BOOLEAN DEFAULT FALSE NOT NULL,
      verification_method VARCHAR(50),
      average_rating DECIMAL(3,2) DEFAULT 5.00,
      total_ratings INT DEFAULT 0 NOT NULL,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
    )
  `);

  // Criar tabela de viagens
  await connection.execute(`
    CREATE TABLE trips (
      id INT AUTO_INCREMENT PRIMARY KEY,
      creator_id INT NOT NULL,
      title VARCHAR(255) NOT NULL,
      localidade VARCHAR(255) NOT NULL,
      cover_image TEXT,
      start_date TIMESTAMP NOT NULL,
      end_date TIMESTAMP NOT NULL,
      budget INT,
      budget_breakdown JSON,
      max_participants INT NOT NULL,
      current_participants INT DEFAULT 1 NOT NULL,
      description TEXT NOT NULL,
      travel_style VARCHAR(100) NOT NULL,
      shared_costs JSON,
      planned_activities JSON,
      status VARCHAR(50) DEFAULT 'open' NOT NULL,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (creator_id) REFERENCES users(id)
    )
  `);

  // Criar tabela de participantes
  await connection.execute(`
    CREATE TABLE trip_participants (
      id INT AUTO_INCREMENT PRIMARY KEY,
      trip_id INT NOT NULL,
      user_id INT NOT NULL,
      status VARCHAR(50) DEFAULT 'pending' NOT NULL,
      joined_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (trip_id) REFERENCES trips(id),
      FOREIGN KEY (user_id) REFERENCES users(id)
    )
  `);

  // Criar tabela de mensagens
  await connection.execute(`
    CREATE TABLE messages (
      id INT AUTO_INCREMENT PRIMARY KEY,
      trip_id INT NOT NULL,
      sender_id INT NOT NULL,
      content TEXT NOT NULL,
      sent_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (trip_id) REFERENCES trips(id),
      FOREIGN KEY (sender_id) REFERENCES users(id)
    )
  `);

  // Criar tabela de solicitaÃ§Ãµes
  await connection.execute(`
    CREATE TABLE trip_requests (
      id INT AUTO_INCREMENT PRIMARY KEY,
      trip_id INT NOT NULL,
      user_id INT NOT NULL,
      message TEXT,
      status VARCHAR(50) DEFAULT 'pending' NOT NULL,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (trip_id) REFERENCES trips(id),
      FOREIGN KEY (user_id) REFERENCES users(id)
    )
  `);

  // Criar tabela de atividades
  await connection.execute(`
    CREATE TABLE activities (
      id INT AUTO_INCREMENT PRIMARY KEY,
      title VARCHAR(255) NOT NULL,
      description TEXT NOT NULL,
      location VARCHAR(255) NOT NULL,
      category VARCHAR(100) NOT NULL,
      duration_hours INT NOT NULL,
      difficulty_level VARCHAR(50) NOT NULL,
      price_range VARCHAR(50) NOT NULL,
      image_url TEXT,
      rating DECIMAL(3,2) DEFAULT 0.00,
      review_count INT DEFAULT 0,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
    )
  `);

  // Criar outras tabelas necessÃ¡rias
  await connection.execute(`
    CREATE TABLE expenses (
      id INT AUTO_INCREMENT PRIMARY KEY,
      trip_id INT NOT NULL,
      paid_by INT NOT NULL,
      amount DECIMAL(10,2) NOT NULL,
      description TEXT NOT NULL,
      category VARCHAR(100) DEFAULT 'other' NOT NULL,
      receipt TEXT,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      settled_at TIMESTAMP,
      FOREIGN KEY (trip_id) REFERENCES trips(id),
      FOREIGN KEY (paid_by) REFERENCES users(id)
    )
  `);

  await connection.execute(`
    CREATE TABLE expense_splits (
      id INT AUTO_INCREMENT PRIMARY KEY,
      expense_id INT NOT NULL,
      user_id INT NOT NULL,
      amount DECIMAL(10,2) NOT NULL,
      paid BOOLEAN DEFAULT FALSE NOT NULL,
      settled_at TIMESTAMP,
      FOREIGN KEY (expense_id) REFERENCES expenses(id),
      FOREIGN KEY (user_id) REFERENCES users(id)
    )
  `);

  await connection.execute(`
    CREATE TABLE user_ratings (
      id INT AUTO_INCREMENT PRIMARY KEY,
      trip_id INT NOT NULL,
      rated_user_id INT NOT NULL,
      rater_user_id INT NOT NULL,
      rating INT NOT NULL,
      comment TEXT,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (trip_id) REFERENCES trips(id),
      FOREIGN KEY (rated_user_id) REFERENCES users(id),
      FOREIGN KEY (rater_user_id) REFERENCES users(id)
    )
  `);

  await connection.execute(`
    CREATE TABLE activity_reviews (
      id INT AUTO_INCREMENT PRIMARY KEY,
      activity_id INT NOT NULL,
      user_id INT NOT NULL,
      rating INT NOT NULL,
      comment TEXT,
      photos JSON,
      visit_date DATE,
      helpful_count INT DEFAULT 0,
      is_verified BOOLEAN DEFAULT FALSE,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (activity_id) REFERENCES activities(id),
      FOREIGN KEY (user_id) REFERENCES users(id)
    )
  `);

  await connection.execute(`
    CREATE TABLE activity_budget_proposals (
      id INT AUTO_INCREMENT PRIMARY KEY,
      activity_id INT NOT NULL,
      title VARCHAR(255) NOT NULL,
      description TEXT,
      price_per_person DECIMAL(10,2) NOT NULL,
      currency VARCHAR(3) DEFAULT 'BRL' NOT NULL,
      inclusions JSON,
      exclusions JSON,
      vote_count INT DEFAULT 0,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (activity_id) REFERENCES activities(id)
    )
  `);

  await connection.execute(`
    CREATE TABLE referral_codes (
      id INT AUTO_INCREMENT PRIMARY KEY,
      code VARCHAR(50) NOT NULL UNIQUE,
      user_id INT,
      type VARCHAR(50) NOT NULL,
      discount_percentage INT DEFAULT 0,
      max_uses INT DEFAULT 1,
      current_uses INT DEFAULT 0,
      expires_at TIMESTAMP,
      is_active BOOLEAN DEFAULT TRUE,
      created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL,
      FOREIGN KEY (user_id) REFERENCES users(id)
    )
  `);

  console.log("âœ… Todas as tabelas criadas com sucesso!");
}

