        .set({
          average_rating: average_rating,
          total_ratings: allUserRatings.length
        })
        .where(eq(users.id, ratingData.rated_user_id));

      res.json({ message: "AvaliaÃ§Ã£o atualizada com sucesso" });
    } catch (error) {
