import { db } from "./db.js";
import { trips, tripParticipants } from "@shared/schema.js";
import { eq, count, and } from "drizzle-orm";

/**
 * Synchronizes the currentParticipants field with actual accepted participants count
 * This ensures data consistency across the application
 */
export async function syncParticipantsCount() {
  console.log('ðŸ”„ Sincronizando contagem de participantes...');

  try {
    // Get all trips
    const allTrips = await db.select({
      id: trips.id,
      title: trips.title,
      current_participants: trips.current_participants
    }).from(trips);

    console.log(`ðŸ“Š Verificando ${allTrips.length} viagens...`);

    let updatedCount = 0;

    for (const trip of allTrips) {
      // Count actual accepted participants for this trip only
      const [result] = await db
        .select({ count: count() })
        .from(tripParticipants)
        .where(and(
          eq(tripParticipants.trip_id, trip.id),
          eq(tripParticipants.status, 'accepted')
        ));

      const realParticipantsCount = result.count;

      // Only update if counts don't match
      if (trip.current_participants !== realParticipantsCount) {
        await db
          .update(trips)
          .set({ current_participants: realParticipantsCount })
          .where(eq(trips.id, trip.id));

        console.log(`âœ… Viagem "${trip.title}" atualizada: ${trip.current_participants} â†’ ${realParticipantsCount} participantes`);
        updatedCount++;
      }
    }

    if (updatedCount === 0) {
      console.log('âœ… Todas as contagens de participantes jÃ¡ estÃ£o sincronizadas');
    } else {
      console.log(`âœ… SincronizaÃ§Ã£o concluÃ­da: ${updatedCount} viagens atualizadas`);
    }

  } catch (error) {
    console.error('âŒ Erro ao sincronizar participantes:', error);
    throw error;
  }
}

/**
 * Updates currentParticipants for a specific trip based on accepted participants
 */
export async function syncTripParticipants(tripId: number) {
  try {
    const [result] = await db
      .select({ count: count() })
      .from(tripParticipants)
      .where(and(
        eq(tripParticipants.trip_id, tripId),
        eq(tripParticipants.status, 'accepted')
      ));

    const realParticipantsCount = result.count;

    await db
      .update(trips)
      .set({ current_participants: realParticipantsCount })
      .where(eq(trips.id, tripId));

    console.log(`âœ… Viagem ${tripId} sincronizada: ${realParticipantsCount} participantes aceitos`);

    return realParticipantsCount;
  } catch (error) {
    console.error(`âŒ Erro ao sincronizar viagem ${tripId}:`, error);
    throw error;
  }
}

