      .where(eq(activities.location, 'Rio de Janeiro, RJ'));

    console.log(`ðŸ“Š Encontradas ${rioActivities.length} atividades no Rio de Janeiro:`);

    for (const activity of rioActivities) {
      console.log(`\nðŸŽ¯ ${activity.title} (ID: ${activity.id})`);
      console.log(`   ðŸ“ LocalizaÃ§Ã£o: ${activity.location}`);
      console.log(`   ðŸ“‚ Categoria: ${activity.category}`);
      console.log(`   â­ Rating: ${activity.rating}/5 (${activity.reviewCount} reviews)`);
      console.log(`   ðŸ• DuraÃ§Ã£o: ${activity.duration}h`);
      console.log(`   ðŸ’° Faixa de preÃ§o: ${activity.priceRange}`);

      // Get budget proposals for this activity
      const proposals = await db
        .select()
        .from(activityBudgetProposals)
        .where(eq(activityBudgetProposals.activityId, activity.id));

      console.log(`   ðŸ’µ Propostas de orÃ§amento (${proposals.length}):`);
      for (const proposal of proposals) {
        console.log(`     - ${proposal.title}: R$ ${proposal.price} (${proposal.votes} votos)`);
      }

      // Get reviews for this activity
      const reviews = await db
        .select()
        .from(activityReviews)
        .where(eq(activityReviews.activityId, activity.id));

      console.log(`   ðŸ“ AvaliaÃ§Ãµes (${reviews.length}):`);
      for (const review of reviews) {
        console.log(`     - ${review.userName}: ${review.rating}/5 estrelas`);
        console.log(`       "${review.comment.substring(0, 50)}..."`);
      }
    }

    console.log('\nâœ… VerificaÃ§Ã£o concluÃ­da com sucesso!');

  } catch (error) {
    console.error('âŒ Erro na verificaÃ§Ã£o:', error);
  }
}

