// TEMPLATE: Activity Verification Script
// Use this template to verify activities after import

async function verifyActivitiesByLocation(location: string) {
  console.log(`ðŸ” Verificando atividades de ${location}...`);

  try {
    // Get all activities for the location
    const locationActivities = await db
      .select()
      .from(activities)
      .where(eq(activities.location, location));

    console.log(`ðŸ“Š Encontradas ${locationActivities.length} atividades em ${location}:`);

    for (const activity of locationActivities) {
      console.log(`\nðŸŽ¯ ${activity.title} (ID: ${activity.id})`);
      console.log(`   ðŸ“ LocalizaÃ§Ã£o: ${activity.location}`);
      console.log(`   ðŸ“‚ Categoria: ${activity.category}`);
      console.log(`   â­ Rating: ${activity.rating}/5`);
      console.log(`   ðŸ• DuraÃ§Ã£o: ${activity.duration}h`);
      console.log(`   ðŸŽ¯ Dificuldade: ${activity.difficulty}`);
      console.log(`   ðŸ’° Faixa de preÃ§o: ${activity.priceRange}`);
      console.log(`   ðŸ–¼ï¸ Imagem: ${activity.coverImage}`);
      console.log(`   ðŸ“ DescriÃ§Ã£o: ${activity.description?.substring(0, 100)}...`);

      // Get budget proposals for this activity
      const proposals = await db
        .select()
        .from(activityBudgetProposals)
        .where(eq(activityBudgetProposals.activityId, activity.id));

      console.log(`   ðŸ’µ Propostas de orÃ§amento (${proposals.length}):`);
      for (const proposal of proposals) {
        console.log(`     - ${proposal.title}: R$ ${proposal.amount} (${proposal.votes} votos)`);
        console.log(`       ðŸ“‹ ${proposal.description}`);

        // Parse inclusions safely
        try {
          const inclusions = JSON.parse(proposal.inclusions || '[]');
          console.log(`       âœ… Inclui: ${inclusions.join(', ')}`);
        } catch (e) {
          console.log(`       âš ï¸ Erro ao parsear inclusÃµes: ${proposal.inclusions}`);
        }
      }
    }

    console.log('\nâœ… VerificaÃ§Ã£o concluÃ­da com sucesso!');
    console.log(`ðŸŽ‰ Total: ${locationActivities.length} atividades em ${location} verificadas`);

    return locationActivities;

  } catch (error) {
    console.error('âŒ Erro na verificaÃ§Ã£o:', error);
    return [];
  }
}

// FunÃ§Ã£o para verificar todas as atividades
async function verifyAllActivities() {
  console.log('ðŸ” Verificando todas as atividades...');

  try {
    const allActivities = await db.select().from(activities);
    const allProposals = await db.select().from(activityBudgetProposals);

    console.log(`ðŸ“Š Resumo geral:`);
    console.log(`   ðŸŽ¯ Total de atividades: ${allActivities.length}`);
    console.log(`   ðŸ’° Total de propostas: ${allProposals.length}`);

    // Agrupa por localizaÃ§Ã£o
    const byLocation = allActivities.reduce((acc, activity) => {
      acc[activity.location] = (acc[activity.location] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    console.log(`   ðŸ“ Por localizaÃ§Ã£o:`);
    for (const [location, count] of Object.entries(byLocation)) {
      console.log(`     - ${location}: ${count} atividades`);
    }

    // Agrupa por categoria
    const byCategory = allActivities.reduce((acc, activity) => {
      acc[activity.category] = (acc[activity.category] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    console.log(`   ðŸ“‚ Por categoria:`);
    for (const [category, count] of Object.entries(byCategory)) {
      console.log(`     - ${category}: ${count} atividades`);
    }

    return { activities: allActivities, proposals: allProposals };

  } catch (error) {
    console.error('âŒ Erro na verificaÃ§Ã£o geral:', error);
    return null;
  }
}

export { verifyActivitiesByLocation, verifyAllActivities };

