import { useState, useCallback, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertActivityBudgetProposalSchema } from "@shared/schema";
import type { z } from "zod";
import { Plus, DollarSign, ThumbsUp, ThumbsDown, Check, User, Package, Star, TrendingUp } from "lucide-react";
import { useAuth } from "@/hooks/use-auth-snake";
import { useToast } from "@/hooks/use-toast";
import { motion } from "framer-motion";
import { formatBrazilianCurrency, formatBrazilianNumber, formatCurrencyByCode } from "@shared/utils";

type ActivityBudgetProposal = {
  id: number;
  activity_id: number;
  title: string;
  description: string;
  amount: number;
  currency: string;
  price_type: string;
  inclusions: string[];
  exclusions: string[];
  votes: number;
  is_active: boolean;
  created_at: string;
  creator: {
    id: number;
    username: string;
    full_name: string;
  };
};

interface ActivityBudgetProposalsProps {
  activityId: number;
  onSelectProposal?: (proposal: ActivityBudgetProposal) => void;
  selectedProposalId?: number;
  allowMultipleSelection?: boolean;
  onProposalsChange?: (proposals: ActivityBudgetProposal[]) => void;
  includedProposalIds?: number[]; // IDs das propostas jÃ¡ incluÃ­das nas viagens do usuÃ¡rio
}

type ProposalFormData = z.infer<typeof insertActivityBudgetProposalSchema>;

export function ActivityBudgetProposals({ 
  activityId, 
  onSelectProposal, 
  selectedProposalId,
  allowMultipleSelection = false,
  onProposalsChange,
  includedProposalIds = []
}: ActivityBudgetProposalsProps) {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedProposals, setSelectedProposals] = useState<ActivityBudgetProposal[]>([]);
  const [pendingVotes, setPendingVotes] = useState<Map<number, boolean>>(new Map());
  const voteTimeouts = useRef<Map<number, NodeJS.Timeout>>(new Map());
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<ProposalFormData>({
    resolver: zodResolver(insertActivityBudgetProposalSchema),
    defaultValues: {
      title: "",
      description: "",
      amount: 0,
      currency: "BRL",
      price_type: "per_person",
      inclusions: [],
      exclusions: []
    }
  });

  // Fetch proposals
  const { data: proposalsData, isLoading } = useQuery({
    queryKey: ['/api/activities', activityId, 'proposals'],
    queryFn: async () => {
      const response = await fetch(`/api/activities/${activityId}/proposals`);
      if (!response.ok) throw new Error('Falha ao buscar propostas');
      return response.json() as Promise<ActivityBudgetProposal[]>;
    }
  });

  // Filter unique proposals to avoid duplicate keys
  const proposals = proposalsData ? proposalsData.filter((proposal, index, arr) => 
    arr.findIndex(p => p.id === proposal.id) === index
  ) : [];

  // Check if user has voted on individual proposals
  const { data: proposalVotes } = useQuery({
    queryKey: ['/api/proposals', 'user-votes', activityId],
    queryFn: async () => {
      if (!user || !proposals || proposals.length === 0) return {};

      const votePromises = proposals.map(async (proposal) => {
        const response = await fetch(`/api/proposals/${proposal.id}/user-vote`, {
          credentials: 'include',
        });
        if (!response.ok) throw new Error('Failed to fetch vote');
        const data = await response.json();
        return { proposalId: proposal.id, hasVoted: data.hasVoted, vote: data.vote };
      });

      const votes = await Promise.all(votePromises);
      return votes.reduce((acc, vote) => {
        acc[vote.proposalId] = vote;
        return acc;
      }, {} as Record<number, { hasVoted: boolean; vote: any }>);
    },
    enabled: !!user && !!proposals && proposals.length > 0,
  });

  // Create proposal mutation
  const createProposal = useMutation({
    mutationFn: async (data: ProposalFormData) => {
      const response = await fetch(`/api/activities/${activityId}/proposals`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });
      if (!response.ok) throw new Error('Falha ao criar proposta');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/activities', activityId, 'proposals'] });
      setIsDialogOpen(false);
      form.reset();
      toast({ title: "Proposta criada com sucesso!" });
    },
    onError: () => {
      toast({ 
        title: "Erro ao criar proposta", 
        description: "Tente novamente mais tarde",
        variant: "destructive" 
      });
    }
  });

  // Vote mutation
  const voteProposal = useMutation({
    mutationFn: async ({ proposalId, increment }: { proposalId: number; increment: boolean }) => {
      const response = await fetch(`/api/proposals/${proposalId}/vote`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ increment })
      });
      if (!response.ok) throw new Error('Falha ao votar');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/activities', activityId, 'proposals'] });
      queryClient.invalidateQueries({ queryKey: ['/api/proposals', 'user-votes', activityId] });
    },
    onError: (error: any) => {
      toast({ 
        title: "Erro ao votar", 
        description: "Tente novamente mais tarde",
        variant: "destructive" 
      });
    }
  });

  const onSubmit = (data: ProposalFormData) => {
    console.log('ðŸ” Dados para envio:', data);
    createProposal.mutate(data);
  };

  // Debounced vote handler - only commits vote after user stops clicking
  const debouncedVote = useCallback((proposalId: number, increment: boolean) => {
    // Clear existing timeout for this proposal
    const existingTimeout = voteTimeouts.current.get(proposalId);
    if (existingTimeout) {
      clearTimeout(existingTimeout);
    }

    // Set new timeout to commit vote after delay
    const newTimeout = setTimeout(() => {
      voteProposal.mutate({ proposalId, increment });
      setPendingVotes(prev => {
        const newMap = new Map(prev);
        newMap.delete(proposalId);
        return newMap;
      });
      voteTimeouts.current.delete(proposalId);
    }, 800); // 800ms delay to commit vote

    voteTimeouts.current.set(proposalId, newTimeout);
  }, [voteProposal]);

  const handleVote = (proposalId: number, increment: boolean) => {
    if (!user) {
      toast({ 
        title: "FaÃ§a login para votar", 
        variant: "destructive" 
      });
      return;
    }

    // Update pending vote state immediately for visual feedback
    setPendingVotes(prev => {
      const newMap = new Map(prev);
      const currentVoteStatus = getProposalVoteStatus(proposalId);
      newMap.set(proposalId, !currentVoteStatus.hasVoted);
      return newMap;
    });

    // Trigger debounced vote
    debouncedVote(proposalId, increment);
  };

  const getProposalVoteStatus = (proposalId: number) => {
    const pendingVote = pendingVotes.get(proposalId);
    const actualVote = proposalVotes?.[proposalId] || { hasVoted: false, vote: null };

    // If there's a pending vote, use that state for visual feedback
    if (pendingVote !== undefined) {
      return { hasVoted: pendingVote, vote: actualVote.vote };
    }

    return actualVote;
  };

  // Clean up timeouts on unmount
  useEffect(() => {
    return () => {
      voteTimeouts.current.forEach(timeout => clearTimeout(timeout));
    };
  }, []);

  // Handle multiple proposal selection
  const handleProposalToggle = (proposal: ActivityBudgetProposal, selected: boolean) => {
    if (!allowMultipleSelection) {
      onSelectProposal?.(proposal);
      return;
    }

    let newSelected: ActivityBudgetProposal[];
    if (selected) {
      newSelected = [...selectedProposals, proposal];
    } else {
      newSelected = selectedProposals.filter(p => p.id !== proposal.id);
    }

    setSelectedProposals(newSelected);
    onProposalsChange?.(newSelected);
  };

  const isProposalSelected = (proposalId: number) => {
    if (!allowMultipleSelection) {
      return selectedProposalId === proposalId;
    }
    return selectedProposals.some(p => p.id === proposalId);
  };

  const calculateSelectedTotal = () => {
    return selectedProposals.reduce((total, proposal) => {
      return total + (Number(proposal.amount) || 0);
    }, 0);
  };

  // Helper function to safely parse JSON arrays
  const safeParseArray = (data: any): string[] => {
    if (Array.isArray(data)) return data;
    if (typeof data === 'string') {
      try {
        let parsed = JSON.parse(data);
        if (typeof parsed === 'string') {
          parsed = JSON.parse(parsed);
        }
        return Array.isArray(parsed) ? parsed : [];
      } catch {
        return [];
      }
    }
    return [];
  };

  // Calculate best value proposal
  const getBestValueProposal = () => {
    if (!proposals || proposals.length === 0) return null;
    return proposals.reduce((best, current) => {
      if (!best) return current;
      const bestScore = best.votes / (Number(best.amount) || 1);
      const currentScore = current.votes / (Number(current.amount) || 1);
      return currentScore > bestScore ? current : best;
    }, null as ActivityBudgetProposal | null);
  };

