import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { ActivityBudgetProposals } from "./activity-budget-proposals";
import { Plane, MapPin, Calendar, Users, DollarSign, Plus } from "lucide-react";
import { useAuth } from "@/hooks/use-auth-snake";
import { useToast } from "@/hooks/use-toast";
import { getParticipantsForBudgetCalculation } from "@/lib/trip-utils";
import { formatBrazilianCurrency, formatBrazilianNumber, formatCurrencyByCode } from "@shared/utils";

type Activity = {
  id: number;
  title: string;
  description: string;
  location: string;
  category: string;
  images: string[];
};

type Trip = {
  id: number;
  title: string;
  localidade: string;
  start_date: string;
  end_date: string;
  cover_image: string;
  max_participants: number;
  budget: number;
};

type ActivityBudgetProposal = {
  id: number;
  activityId: number;
  title: string;
  description: string;
  amount: number;
  currency: string;
  priceType: string;
  inclusions: string[];
  exclusions: string[];
  votes: number;
  creator: {
    id: number;
    username: string;
    full_name: string;
  };
};

interface AddActivityToTripProps {
  activity: Activity;
  isOpen: boolean;
  onClose: () => void;
  selectedProposals?: ActivityBudgetProposal[];
}

export function AddActivityToTrip({ activity, isOpen, onClose, selectedProposals = [] }: AddActivityToTripProps) {
  const [selectedTripId, setSelectedTripId] = useState<number | null>(null);
  const [selectedProposal, setSelectedProposal] = useState<ActivityBudgetProposal | null>(null);
  const [step, setStep] = useState<'trip' | 'proposal' | 'confirm'>('trip');

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user trips in same location as activity (like in the trip page)
  const { data: userTrips = [], isLoading: tripsLoading } = useQuery({
    queryKey: ['/api/users', user?.id, 'trips-in-location', activity.location],
    queryFn: async () => {
      if (!user) return [];
      const response = await fetch(`/api/users/${user.id}/trips-in-location?location=${encodeURIComponent(activity.location)}`);
      if (!response.ok) {
        if (response.status === 401) return [];
        throw new Error('Falha ao buscar viagens');
      }
      const result = await response.json();
      console.log('ðŸ” AddActivityToTrip trips in location result:', result);
      return result;
    },
    enabled: !!user && isOpen
  });

  // Add activity to trip mutation
  const addActivityToTrip = useMutation({
    mutationFn: async () => {
      if (!selectedTripId) {
        throw new Error('Selecione uma viagem');
      }

      const selectedTrip = Array.isArray(userTrips) ? userTrips.find(trip => trip.id === selectedTripId) : undefined;
      if (!selectedTrip) {
        throw new Error('Viagem nÃ£o encontrada');
      }

      // Use pre-selected proposals or single selected proposal
      const proposalsToAdd = selectedProposals && selectedProposals.length > 0 
        ? selectedProposals 
        : selectedProposal 
        ? [selectedProposal] 
        : [];

      if (proposalsToAdd.length === 0) {
        throw new Error('Selecione pelo menos uma proposta');
      }

      // Add each proposal as separate activity
      const promises = proposalsToAdd.map(async (proposal) => {
        const participants = getParticipantsForBudgetCalculation(selectedTrip);
        const amount = typeof proposal.amount === 'number' ? proposal.amount : parseFloat(proposal.amount || '0');
        const totalCost = proposal.priceType === "per_person" 
          ? amount * participants
          : amount;

        console.log('ðŸ” Calculando custo da atividade:', {
          proposalAmount: proposal.amount,
          priceType: proposal.priceType,
          participants,
          totalCost
        });

        const response = await fetch(`/api/trips/${selectedTripId}/activities`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            activityId: activity.id,
            budgetProposalId: proposal.id,
            participants,
            totalCost,
            notes: `Atividade adicionada: ${activity.title} - ${proposal.title}`
          })
        });

        if (!response.ok) throw new Error('Falha ao adicionar atividade');
        return response.json();
      });

      return Promise.all(promises);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/trips'] });

      const proposalsCount = selectedProposals && selectedProposals.length > 0 
        ? selectedProposals.length 
        : 1;

      toast({ 
        title: "Atividade adicionada com sucesso!", 
        description: `${activity.title} foi adicionada Ã  sua viagem${proposalsCount > 1 ? ` com ${proposalsCount} propostas de orÃ§amento` : ''}.` 
      });
      handleClose();
    },
    onError: (error) => {
      toast({ 
        title: "Erro ao adicionar atividade", 
        description: error.message,
        variant: "destructive" 
      });
    }
  });

  const handleClose = () => {
    setStep('trip');
    setSelectedTripId(null);
    setSelectedProposal(null);
    onClose();
  };

  const handleTripSelect = (tripId: string) => {
    setSelectedTripId(parseInt(tripId));
    // If we have pre-selected proposals, skip to confirm step
    if (selectedProposals && selectedProposals.length > 0) {
      setStep('confirm');
    } else {
      setStep('proposal');
    }
  };

  const handleProposalSelect = (proposal: ActivityBudgetProposal) => {
    setSelectedProposal(proposal);
    setStep('confirm');
  };

  const handleConfirm = () => {
    addActivityToTrip.mutate();
  };

  const selectedTrip = Array.isArray(userTrips) ? userTrips.find(trip => trip.id === selectedTripId) : undefined;

  // Debug logging
  if (isOpen && user) {
    console.log('ðŸ” AddActivityToTrip Debug:', {
      userTrips: userTrips,
      isArray: Array.isArray(userTrips),
      length: Array.isArray(userTrips) ? userTrips.length : 'not array',
      tripsLoading
    });
  }

  if (!user) {
    return (
      <Dialog open={isOpen} onOpenChange={handleClose}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>FaÃ§a login para adicionar atividades</DialogTitle>
            <DialogDescription>
              VocÃª precisa estar logado para adicionar atividades Ã s suas viagens
            </DialogDescription>
          </DialogHeader>
          <div className="py-6 text-center">
            <Plane className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600 mb-4">
              VocÃª precisa estar logado para adicionar atividades Ã s suas viagens.
            </p>
            <Button onClick={handleClose}>Entendi</Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Plane className="h-5 w-5" />
            Adicionar {activity.title} Ã  Viagem
          </DialogTitle>
          <DialogDescription>
            Selecione uma viagem para adicionar esta atividade
          </DialogDescription>
        </DialogHeader>

        {step === 'trip' && (
          <div className="space-y-4">
            <div className="flex items-center gap-2 text-sm text-gray-600 mb-4">
              <MapPin className="h-4 w-4" />
              <span>Buscando suas viagens em {activity.location}</span>
            </div>

            {tripsLoading ? (
              <div className="space-y-3">
                {[1, 2, 3].map(i => (
                  <div key={i} className="h-24 bg-gray-100 rounded-lg animate-pulse" />
                ))}
              </div>
            ) : !Array.isArray(userTrips) || userTrips.length === 0 ? (
              <Card>
                <CardContent className="py-8 text-center">
                  <Plane className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-lg font-medium text-gray-600 mb-2">
                    Nenhuma viagem encontrada
                  </h4>
                  <p className="text-gray-500 mb-4">
                    VocÃª nÃ£o tem viagens programadas para {activity.location}.
                  </p>
                  <p className="text-sm text-gray-400">
                    Para adicionar esta atividade, crie uma viagem para esta localidade primeiro.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="space-y-3">
                <h3 className="font-semibold">Selecione uma viagem:</h3>
                <div className="grid gap-3">
                  {Array.isArray(userTrips) && userTrips.map((trip) => (
                    <Card 
                      key={trip.id} 
                      className="cursor-pointer hover:shadow-md transition-shadow"
                      onClick={() => handleTripSelect(trip.id.toString())}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-center gap-4">
                          <img 
                            src={trip.cover_image} 
                            alt={trip.title}
                            className="w-16 h-16 rounded-lg object-cover"
                          />
                          <div className="flex-1">
                            <h4 className="font-semibold">{trip.title}</h4>
                            <p className="text-sm text-gray-600 flex items-center gap-1">
                              <MapPin className="h-3 w-3" />
                              {trip.localidade}
                            </p>
                            <div className="flex items-center gap-4 mt-2 text-xs text-gray-500">
                              <span className="flex items-center gap-1">
                                <Calendar className="h-3 w-3" />
                                {new Date(trip.start_date).toLocaleDateString('pt-BR')}
                              </span>
                              <span className="flex items-center gap-1">
                                <Users className="h-3 w-3" />
                                atÃ© {trip.max_participants} pessoas
                              </span>
                              <span className="flex items-center gap-1">
                                <DollarSign className="h-3 w-3" />
                                {formatBrazilianCurrency(trip.budget)}
                              </span>
                            </div>
                          </div>
                          <Button 
                            size="sm" 
                            className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white shadow-md hover:shadow-lg transition-all duration-200"
                          >
                            <Plus className="h-3 w-3 mr-1" />
                            Selecionar
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {step === 'proposal' && selectedTrip && (
          <div className="space-y-4">
            <div className="flex items-center gap-2 mb-4">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => setStep('trip')}
              >
                â† Voltar
              </Button>
              <div>
                <h3 className="font-semibold">{selectedTrip.title}</h3>
                <p className="text-sm text-gray-600">{selectedTrip.localidade}</p>
              </div>
            </div>

            <ActivityBudgetProposals 
              activityId={activity.id}
              onSelectProposal={handleProposalSelect}
            />
          </div>
        )}

        {step === 'confirm' && selectedTrip && ((selectedProposals && selectedProposals.length > 0) || selectedProposal) && (
          <div className="space-y-6">
            <div className="flex items-center gap-2 mb-4">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => setStep('proposal')}
              >
                â† Voltar
              </Button>
              <h3 className="font-semibold">Confirmar AdiÃ§Ã£o</h3>
            </div>

            <div className="grid grid-cols-2 gap-6">
              {/* Trip Details */}
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Viagem Selecionada</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex items-center gap-3 mb-4">
                    <img 
                      src={selectedTrip.cover_image} 
                      alt={selectedTrip.title}
                      className="w-12 h-12 rounded-lg object-cover"
                    />
                    <div>
                      <h4 className="font-semibold">{selectedTrip.title}</h4>
                      <p className="text-sm text-gray-600">{selectedTrip.localidade}</p>
                    </div>
                  </div>
                  <div className="space-y-2 text-sm">
                    <div className="flex items-center gap-2">
                      <Calendar className="h-4 w-4 text-gray-500" />
                      <span>
                        {new Date(selectedTrip.start_date).toLocaleDateString('pt-BR')} - {' '}
                        {new Date(selectedTrip.end_date).toLocaleDateString('pt-BR')}
                      </span>
                    </div>
                    <div className="flex items-center gap-2">
                      <DollarSign className="h-4 w-4 text-gray-500" />
                      <span>OrÃ§amento: {formatBrazilianCurrency(selectedTrip.budget)}</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Proposal Details */}
              <Card>
