import { useState } from 'react';
import { Star, MapPin, Calendar, Users, DollarSign, Clock, Heart, Share2 } from 'lucide-react';
import { Card, CardContent } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { Avatar, AvatarFallback, AvatarImage } from './ui/avatar';
import { LoadingSpinner } from './ui/loading-spinner';
import { OptimizedImage } from './optimized-image';
import { cn } from '@/lib/utils';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import { formatBrazilianCurrency, formatBrazilianNumber } from '@shared/utils';
import { getDaysUntilDate } from '@/lib/date-utils';

interface Trip {
  id: number;
  title: string;
  description: string;
  localidade: string;
  start_date: string;
  end_date: string;
  budget: number;
  max_participants: number;
  current_participants: number;
  travel_style: string;
  creator: {
    id: number;
    full_name: string;
    profile_photo?: string;
    average_rating: number;
    is_verified: boolean;
  };
  cover_image?: string;
}

interface EnhancedTripCardProps {
  trip: Trip;
  onSelect?: (trip: Trip) => void;
  onFavorite?: (tripId: number) => void;
  onShare?: (tripId: number) => void;
  isFavorited?: boolean;
  isLoading?: boolean;
  className?: string;
  variant?: 'default' | 'compact' | 'featured';
}

export function EnhancedTripCard({ 
  trip, 
  onSelect, 
  onFavorite,
  onShare,
  isFavorited = false,
  isLoading = false,
  className,
  variant = 'default'
}: EnhancedTripCardProps) {
  const [imageError, setImageError] = useState(false);

  const handleFavoriteClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    onFavorite?.(trip.id);
  };

  const handleShareClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    onShare?.(trip.id);
  };

  const travelStyleColors = {
    'praia': 'bg-blue-100 text-blue-800',
    'aventura': 'bg-green-100 text-green-800',
    'urbanas': 'bg-gray-100 text-gray-800',
    'cultural': 'bg-purple-100 text-purple-800',
    'natureza': 'bg-emerald-100 text-emerald-800',
    'neve': 'bg-cyan-100 text-cyan-800'
  };

  const getDaysUntilTrip = () => getDaysUntilDate(trip.start_date);

  const daysUntil = getDaysUntilTrip();
  const isUpcoming = daysUntil > 0;
  const spotsLeft = trip.max_participants - trip.current_participants;

  const cardHeight = variant === 'compact' ? 'h-64' : variant === 'featured' ? 'h-80' : 'h-72';

  return (
    <Card 
      className={cn(
        "group cursor-pointer transition-all duration-300 hover:shadow-xl hover:-translate-y-2",
        "border-gray-200 hover:border-blue-300 overflow-hidden",
        variant === 'featured' && "ring-2 ring-blue-200 shadow-lg",
        isLoading && "pointer-events-none opacity-50",
        cardHeight,
        className
      )}
      onClick={() => onSelect?.(trip)}
    >
      <div className="relative h-2/3">
        {/* Cover Image */}
        <OptimizedImage
          src={trip.cover_image || `https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=400&h=300&fit=crop&crop=center`}
          alt={trip.title}
          className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
          fallbackSrc="https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=400&h=300&fit=crop&crop=center"
        />

        {/* Gradient Overlay */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent" />

        {/* Action Buttons */}
        <div className="absolute top-3 right-3 flex space-x-2">
          <Button
            size="sm"
            variant="ghost"
            className="h-8 w-8 p-0 bg-white/90 hover:bg-white"
            onClick={handleShareClick}
          >
            <Share2 className="w-4 h-4 text-gray-600" />
          </Button>

          <Button
            size="sm"
            variant="ghost"
            className="h-8 w-8 p-0 bg-white/90 hover:bg-white"
            onClick={handleFavoriteClick}
          >
            <Heart 
              className={cn(
                "w-4 h-4 transition-colors",
                isFavorited ? "fill-red-500 text-red-500" : "text-gray-600"
              )} 
            />
          </Button>
        </div>

        {/* Travel Style Badge */}
        <Badge 
          className={cn(
            "absolute top-3 left-3",
            travelStyleColors[trip.travel_style as keyof typeof travelStyleColors] || 'bg-gray-100 text-gray-800'
          )}
        >
          {trip.travel_style}
        </Badge>

        {/* Featured Badge */}
        {variant === 'featured' && (
          <Badge className="absolute top-12 left-3 bg-gradient-to-r from-yellow-400 to-orange-500 text-white">
            â­ Destaque
          </Badge>
        )}

        {/* Days Until Trip */}
        {isUpcoming && daysUntil <= 30 && (
          <Badge 
            variant="secondary" 
            className="absolute bottom-3 right-3 bg-white/90 text-gray-800"
          >
            {daysUntil === 1 ? 'AmanhÃ£' : `${daysUntil} dias`}
          </Badge>
        )}

        {/* Loading overlay */}
        {isLoading && (
          <div className="absolute inset-0 bg-white/80 flex items-center justify-center rounded-t-lg">
            <LoadingSpinner size="lg" />
          </div>
        )}
      </div>

      <CardContent className="p-4 h-1/3 flex flex-col justify-between">
        <div className="space-y-2">
          {/* Title and Location */}
          <div>
            <h3 className="font-semibold text-lg leading-tight text-gray-900 group-hover:text-blue-600 transition-colors line-clamp-1">
              {trip.title}
            </h3>
            <div className="flex items-center text-sm text-gray-500 mt-1">
              <MapPin className="w-4 h-4 mr-1 flex-shrink-0" />
              <span className="truncate">{trip.localidade}</span>
            </div>
          </div>

          {/* Dates */}
          <div className="flex items-center text-sm text-gray-600">
            <Calendar className="w-4 h-4 mr-1 flex-shrink-0" />
            <span className="truncate">
              {format(new Date(trip.start_date), 'dd MMM', { locale: ptBR })} - {format(new Date(trip.end_date), 'dd MMM', { locale: ptBR })}
            </span>
          </div>
        </div>

        {/* Bottom Section */}
        <div className="space-y-3">
          {/* Creator Info */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Avatar className="w-6 h-6">
                <AvatarImage src={trip.creator.profile_photo} />
                <AvatarFallback className="text-xs bg-blue-100 text-blue-600">
                  {trip.creator.full_name.split(' ').map(n => n[0]).join('')}
                </AvatarFallback>
              </Avatar>
              <div className="flex items-center space-x-1">
                <span className="text-sm text-gray-600 truncate max-w-20">
                  {trip.creator.full_name.split(' ')[0]}
                </span>
                {trip.creator.is_verified && (
                  <div className="w-4 h-4 bg-green-500 rounded-full flex items-center justify-center">
                    <span className="text-white text-xs">âœ“</span>
                  </div>
                )}
              </div>
            </div>

            {/* Creator Rating */}
            <div className="flex items-center">
              <Star className="w-3 h-3 text-yellow-400 fill-current" />
              <span className="ml-1 text-sm font-medium text-gray-900">
                {formatBrazilianNumber(trip.creator.average_rating).replace(',00', ',0')}
              </span>
            </div>
          </div>

          {/* Trip Stats */}
          <div className="flex items-center justify-between text-sm">
            {/* Budget */}
            <div className="flex items-center text-green-600 font-medium">
              <DollarSign className="w-4 h-4 mr-1" />
              <span>{formatBrazilianCurrency(trip.budget)}</span>
            </div>

            {/* Participants */}
            <div className="flex items-center space-x-4">
              <div className="flex items-center text-gray-600">
                <Users className="w-4 h-4 mr-1" />
                <span>{trip.current_participants}/{trip.max_participants}</span>
              </div>

              {spotsLeft > 0 && (
                <Badge 
                  variant="outline" 
                  className={cn(
                    "text-xs",
                    spotsLeft === 1 ? "border-orange-300 text-orange-600" : "border-green-300 text-green-600"
                  )}
                >
                  {spotsLeft} {spotsLeft === 1 ? 'vaga' : 'vagas'}
                </Badge>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/expense-manager.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth-snake";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Plus, Receipt, DollarSign, Users, Calculator, TrendingDown, TrendingUp, Check, X, RefreshCw } from "lucide-react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { expenseCategories } from "@shared/schema";
import { formatBrazilianCurrency, formatBrazilianNumber, calculatePerPerson, sumValues, parseNumber } from "@shared/utils";

interface ExpenseManagerProps {
  tripId: number;
  participants: any[];
}

export function ExpenseManager({ tripId, participants }: ExpenseManagerProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isAddingExpense, setIsAddingExpense] = useState(false);
  const [newExpense, setNewExpense] = useState({
    amount: "",
    description: "",
    category: "other",
    splitWith: participants.map(p => p.user_id || p.userId),
    receipt: null as string | null,
    splitEqually: true,
  });

  // Fetch expenses
  const { data: expenses = [], isLoading: expensesLoading } = useQuery({
    queryKey: ['/api/trips', tripId, 'expenses'],
    queryFn: async () => {
      const response = await fetch(`/api/trips/${tripId}/expenses`);
      if (!response.ok) throw new Error('Falha ao buscar despesas');
      return response.json();
    },
    staleTime: 0, // Always fetch fresh data
    refetchOnWindowFocus: true,
  });

  // Fetch balances
  const { data: balances = [], isLoading: balancesLoading, error: balancesError, refetch: refetchBalances } = useQuery({
    queryKey: ['/api/trips', tripId, 'balances'],
    queryFn: async () => {
      const response = await fetch(`/api/trips/${tripId}/balances`, {
        cache: 'no-store',
        headers: {
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });
      if (!response.ok) throw new Error('Falha ao buscar balanÃ§os');
      const data = await response.json();
      return data;
    },
    staleTime: 0, // Always fetch fresh data
    refetchOnWindowFocus: true,
    refetchInterval: 5000, // Refresh every 5 seconds
  });

  // Create expense mutation
  const createExpenseMutation = useMutation({
    mutationFn: async (expenseData: any) => {
      const response = await apiRequest('POST', `/api/trips/${tripId}/expenses`, expenseData);
      return response.json();
    },
    onSuccess: () => {
      // Invalidate both queries to ensure data refresh
      queryClient.invalidateQueries({ queryKey: ['/api/trips', tripId, 'expenses'] });
      queryClient.invalidateQueries({ queryKey: ['/api/trips', tripId, 'balances'] });

      // Also refetch immediately
      queryClient.refetchQueries({ queryKey: ['/api/trips', tripId, 'expenses'] });
      queryClient.refetchQueries({ queryKey: ['/api/trips', tripId, 'balances'] });

      setIsAddingExpense(false);
      setNewExpense({
        amount: "",
        description: "",
        category: "other",
        splitWith: participants.map(p => p.user_id || p.userId),
        receipt: null,
        splitEqually: true,
      });
      toast({
        title: "Despesa adicionada",
        description: "A despesa foi registrada com sucesso.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao adicionar despesa",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update expense split mutation
  const updateSplitMutation = useMutation({
    mutationFn: async ({ splitId, paid }: { splitId: number; paid: boolean }) => {
      const response = await apiRequest('PATCH', `/api/expense-splits/${splitId}`, { paid });
      return response.json();
    },
    onSuccess: () => {
      // Invalidate and refetch both queries
      queryClient.invalidateQueries({ queryKey: ['/api/trips', tripId, 'expenses'] });
      queryClient.invalidateQueries({ queryKey: ['/api/trips', tripId, 'balances'] });
      queryClient.refetchQueries({ queryKey: ['/api/trips', tripId, 'expenses'] });
      queryClient.refetchQueries({ queryKey: ['/api/trips', tripId, 'balances'] });
    },
  });

  const handleAddExpense = () => {
    if (!newExpense.amount || !newExpense.description) {
      toast({
        title: "Campos obrigatÃ³rios",
        description: "Por favor, preencha o valor e a descriÃ§Ã£o.",
        variant: "destructive",
      });
      return;
    }

    createExpenseMutation.mutate({
      amount: parseNumber(newExpense.amount),
      description: newExpense.description,
      category: newExpense.category,
      splitWith: newExpense.splitEqually ? 'all' : newExpense.splitWith,
      receipt: newExpense.receipt,
    });
  };

  const handleSplitParticipantToggle = (userId: number) => {
    setNewExpense(prev => ({
      ...prev,
      splitWith: prev.splitWith.includes(userId)
        ? prev.splitWith.filter(id => id !== userId)
        : [...prev.splitWith, userId]
    }));
  };

  // Using shared formatBrazilianCurrency utility directly instead of local wrapper

  // Calculate who owes whom
  const calculateSettlements = () => {
    const settlements: { from: any; to: any; amount: number }[] = [];
    // Create copies of balances to avoid modifying the original data
    const creditors = balances.filter((b: any) => b.balance > 0).map((b: any) => ({ ...b })).sort((a: any, b: any) => b.balance - a.balance);
    const debtors = balances.filter((b: any) => b.balance < 0).map((b: any) => ({ ...b })).sort((a: any, b: any) => a.balance - b.balance);

    let i = 0, j = 0;
    while (i < creditors.length && j < debtors.length) {
      const credit = creditors[i].balance;
      const debt = Math.abs(debtors[j].balance);
      const settlementAmount = Math.min(credit, debt);

      settlements.push({
        from: debtors[j].user,
        to: creditors[i].user,
        amount: settlementAmount,
      });

      creditors[i].balance -= settlementAmount;
      debtors[j].balance += settlementAmount;

      if (creditors[i].balance === 0) i++;
      if (debtors[j].balance === 0) j++;
    }

    return settlements;
  };

  const settlements = calculateSettlements();

  return (
    <div className="space-y-6">
      {/* Balance Summary */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Calculator className="h-5 w-5" />
              Resumo de Saldos
            </div>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => refetchBalances()}
              disabled={balancesLoading}
            >
              <RefreshCw className={`h-4 w-4 ${balancesLoading ? 'animate-spin' : ''}`} />
            </Button>
          </CardTitle>
          <div className="text-sm text-gray-600 mt-2">
            <div className="flex items-center gap-4">
