                  <div key={balance.user_id} className="flex items-center gap-3 p-3 rounded-lg bg-gray-50 dark:bg-gray-800">
                    <Avatar className="h-10 w-10">
                      <AvatarImage src={balance.user.profile_photo || ""} />
                      <AvatarFallback>
                        {balance.user.full_name?.substring(0, 2).toUpperCase() || balance.user.username?.substring(0, 2).toUpperCase() || "??"}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <p className="font-medium">{balance.user.full_name || balance.user.username}</p>
                      <p className={`text-sm font-semibold ${
                        balance.balance > 0 ? 'text-green-600' : balance.balance < 0 ? 'text-red-600' : 'text-gray-600'
                      }`}>
                        {balance.balance > 0 && '+ '}
                        {formatBrazilianCurrency(balance.balance)}
                      </p>

                      <p className="text-xs text-gray-500">
                        {balance.balance > 0 ? 'Tem a receber' : balance.balance < 0 ? 'Tem a pagar' : 'Sem pendÃªncias'}
                      </p>
                    </div>
                    {balance.balance > 0 && <TrendingUp className="h-4 w-4 text-green-600" />}
                    {balance.balance < 0 && <TrendingDown className="h-4 w-4 text-red-600" />}
                  </div>
                ))}
              </div>

              {settlements.length > 0 && (
                <>
                  <Separator />
                  <div>
                    <h4 className="font-medium mb-3 flex items-center gap-2">
                      <Users className="h-4 w-4" />
                      Acertos Sugeridos
                    </h4>
                    <div className="space-y-2">
                      {settlements.map((settlement, index) => (
                        <div key={index} className="flex items-center gap-2 text-sm">
                          <span className="font-medium">{settlement.from.full_name}</span>
                          <span className="text-gray-600">deve</span>
                          <span className="font-semibold text-primary">{formatBrazilianCurrency(settlement.amount)}</span>
                          <span className="text-gray-600">para</span>
                          <span className="font-medium">{settlement.to.full_name}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                </>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Add Expense Button */}
      <div className="flex justify-end">
        <Dialog open={isAddingExpense} onOpenChange={setIsAddingExpense}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Adicionar Despesa
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>Nova Despesa</DialogTitle>
            </DialogHeader>
            <div className="space-y-4 mt-4">
              <div>
                <Label htmlFor="amount">Valor (R$)</Label>
                <Input
                  id="amount"
                  type="number"
                  step="0.01"
                  placeholder="0,00"
                  value={newExpense.amount}
                  onChange={(e) => setNewExpense(prev => ({ ...prev, amount: e.target.value }))}
                />
              </div>

              <div>
                <Label htmlFor="description">DescriÃ§Ã£o</Label>
                <Textarea
                  id="description"
                  placeholder="O que foi pago?"
                  value={newExpense.description}
                  onChange={(e) => setNewExpense(prev => ({ ...prev, description: e.target.value }))}
                />
              </div>

              <div>
                <Label htmlFor="category">Categoria</Label>
                <Select
                  value={newExpense.category}
                  onValueChange={(value) => setNewExpense(prev => ({ ...prev, category: value }))}
                >
                  <SelectTrigger id="category">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(expenseCategories).map(([value, label]) => (
                      <SelectItem key={value} value={value}>
                        {label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>Dividir com:</Label>
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Checkbox
                      id="split-equally"
                      checked={newExpense.splitEqually}
                      onCheckedChange={(checked) => setNewExpense(prev => ({ ...prev, splitEqually: checked as boolean }))}
                    />
                    <Label htmlFor="split-equally" className="cursor-pointer">
                      <div className="flex items-center gap-2">
                        <Users className="h-4 w-4" />
                        <span>Dividir igualmente entre todos os participantes</span>
                      </div>
                      <p className="text-xs text-gray-500 mt-1">
                        Incluindo futuros participantes que entrarem na viagem
                      </p>
                    </Label>
                  </div>

                  {!newExpense.splitEqually && (
                    <ScrollArea className="h-32 border rounded-lg p-3">
                      <div className="space-y-2">
                        {participants.map((participant) => (
                          <div key={participant.user_id} className="flex items-center gap-2">
                            <Checkbox
                              id={`split-${participant.user_id}`}
                              checked={newExpense.splitWith.includes(participant.user_id)}
                              onCheckedChange={() => handleSplitParticipantToggle(participant.user_id)}
                            />
                            <Label
                              htmlFor={`split-${participant.user_id}`}
                              className="flex items-center gap-2 cursor-pointer"
                            >
                              <Avatar className="h-6 w-6">
                                <AvatarImage src={participant.user.profile_photo || ""} />
                                <AvatarFallback className="text-xs">
                                  {participant.user.full_name.substring(0, 2).toUpperCase()}
                                </AvatarFallback>
                              </Avatar>
                              <span>{participant.user.full_name}</span>
                            </Label>
                          </div>
                        ))}
                      </div>
                    </ScrollArea>
                  )}
                </div>
              </div>

              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => setIsAddingExpense(false)}
                >
                  Cancelar
                </Button>
                <Button
                  onClick={handleAddExpense}
                  disabled={createExpenseMutation.isPending}
                  className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white shadow-md hover:shadow-lg transition-all duration-200"
                >
                  {createExpenseMutation.isPending ? "Adicionando..." : "Adicionar"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Expenses List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Despesas da Viagem
          </CardTitle>
        </CardHeader>
        <CardContent>
          {expensesLoading ? (
            <div className="text-center py-4">Carregando despesas...</div>
          ) : expenses.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Receipt className="h-12 w-12 mx-auto mb-2 opacity-20" />
              <p>Nenhuma despesa registrada ainda.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {expenses.map((expense: any) => (
                <div key={expense.id} className="border rounded-lg p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div>
                      <h4 className="font-medium">{expense.description}</h4>
                      <p className="text-sm text-gray-600">
                        Pago por {expense.payer.full_name} â€¢ {format(new Date(expense.created_at), "dd 'de' MMM", { locale: ptBR })}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold text-lg">{formatBrazilianCurrency(expense.amount)}</p>
                      <Badge variant="secondary" className="text-xs">
                        {expenseCategories[expense.category as keyof typeof expenseCategories]}
                      </Badge>
                    </div>
                  </div>

                  <Separator className="my-3" />

                  <div>
                    <p className="text-sm font-medium mb-2">DivisÃ£o:</p>
                    <div className="space-y-1">
                      {expense.splits.map((split: any) => (
                        <div key={split.id} className="flex items-center justify-between text-sm">
                          <div className="flex items-center gap-2">
                            <Avatar className="h-6 w-6">
                              <AvatarImage src={split.user.profile_photo || ""} />
                              <AvatarFallback className="text-xs">
                                {split.user.full_name.substring(0, 2).toUpperCase()}
                              </AvatarFallback>
                            </Avatar>
                            <span>{split.user.full_name}</span>
                          </div>
                          <div className="flex items-center gap-2">
                            <span className={split.paid ? 'text-green-600' : 'text-gray-600'}>
                              {formatBrazilianCurrency(split.amount)}
                            </span>
                            {split.paid ? (
                              <Badge variant="outline" className="text-xs text-green-600">
                                <Check className="h-3 w-3 mr-1" />
                                Pago
                              </Badge>
                            ) : (
                              split.user_id === user?.id && (
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => updateSplitMutation.mutate({ splitId: split.id, paid: true })}
                                  disabled={updateSplitMutation.isPending}
                                >
                                  Marcar como pago
                                </Button>
                              )
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

