import { useState } from 'react';
import { Star, MapPin, Clock, Users, Heart } from 'lucide-react';
import { Card, CardContent } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { LoadingSpinner } from './ui/loading-spinner';
import { cn } from '@/lib/utils';
import { formatBrazilianCurrency, formatBrazilianNumber } from '@shared/utils';

interface Activity {
  id: number;
  title: string;
  description: string;
  location: string;
  cover_image: string;
  category: string;
  average_rating: number;
  total_ratings: number;
  estimatedDuration?: string;
  max_participants?: number;
  difficulty?: string;
}

interface ImprovedActivityCardProps {
  activity: Activity;
  onSelect?: (activity: Activity) => void;
  onFavorite?: (activityId: number) => void;
  isFavorited?: boolean;
  isLoading?: boolean;
  className?: string;
}

export function ImprovedActivityCard({ 
  activity, 
  onSelect, 
  onFavorite,
  isFavorited = false,
  isLoading = false,
  className 
}: ImprovedActivityCardProps) {
  const [imageLoading, setImageLoading] = useState(true);
  const [imageError, setImageError] = useState(false);

  const handleFavoriteClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    onFavorite?.(activity.id);
  };

  const difficultyColors = {
    'fÃ¡cil': 'bg-green-100 text-green-800',
    'mÃ©dio': 'bg-yellow-100 text-yellow-800',
    'difÃ­cil': 'bg-red-100 text-red-800'
  };

  return (
    <Card 
      className={cn(
        "group cursor-pointer transition-all duration-300 hover:shadow-lg hover:-translate-y-1",
        "border-gray-200 hover:border-blue-300",
        isLoading && "pointer-events-none opacity-50",
        className
      )}
      onClick={() => onSelect?.(activity)}
    >
      <div className="relative">
        {/* Image with loading states */}
        <div className="relative h-48 overflow-hidden rounded-t-lg">
          {imageLoading && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
              <LoadingSpinner size="md" />
            </div>
          )}

          {!imageError ? (
            <img
              src={activity.cover_image}
              alt={activity.title}
              className={cn(
                "w-full h-full object-cover transition-transform duration-300 group-hover:scale-105",
                imageLoading && "opacity-0"
              )}
              onLoad={() => setImageLoading(false)}
              onError={() => {
                setImageError(true);
                setImageLoading(false);
              }}
            />
          ) : (
            <div className="w-full h-full bg-gradient-to-br from-gray-200 to-gray-300 flex items-center justify-center">
              <div className="text-center text-gray-500">
                <MapPin className="w-8 h-8 mx-auto mb-2" />
                <p className="text-sm">Imagem nÃ£o disponÃ­vel</p>
              </div>
            </div>
          )}

          {/* Favorite button */}
          <Button
            size="sm"
            variant="ghost"
            className="absolute top-2 right-2 h-8 w-8 p-0 bg-white/90 hover:bg-white"
            onClick={handleFavoriteClick}
          >
            <Heart 
              className={cn(
                "w-4 h-4 transition-colors",
                isFavorited ? "fill-red-500 text-red-500" : "text-gray-600"
              )} 
            />
          </Button>

          {/* Category badge */}
          <Badge 
            variant="secondary" 
            className="absolute top-2 left-2 bg-white/90 text-gray-800"
          >
            {activity.category}
          </Badge>

          {/* Difficulty badge */}
          {activity.difficulty && (
            <Badge 
              className={cn(
                "absolute bottom-2 left-2",
                difficultyColors[activity.difficulty as keyof typeof difficultyColors] || 'bg-gray-100 text-gray-800'
              )}
            >
              {activity.difficulty}
            </Badge>
          )}
        </div>

        {/* Loading overlay */}
        {isLoading && (
          <div className="absolute inset-0 bg-white/80 flex items-center justify-center rounded-lg">
            <LoadingSpinner size="lg" />
          </div>
        )}
      </div>

      <CardContent className="p-4">
        <div className="space-y-3">
          {/* Title */}
          <h3 className="font-semibold text-lg leading-tight text-gray-900 group-hover:text-blue-600 transition-colors">
            {activity.title}
          </h3>

          {/* Description */}
          <p className="text-sm text-gray-600 line-clamp-2 leading-relaxed">
            {activity.description}
          </p>

          {/* Location */}
          <div className="flex items-center text-sm text-gray-500">
            <MapPin className="w-4 h-4 mr-1 flex-shrink-0" />
            <span className="truncate">{activity.location}</span>
          </div>

          {/* Rating and metadata */}
          <div className="flex items-center justify-between pt-2">
            <div className="flex items-center space-x-4">
              {/* Rating */}
              <div className="flex items-center">
                <Star className="w-4 h-4 text-yellow-400 fill-current" />
                <span className="ml-1 text-sm font-medium text-gray-900">
                  {formatBrazilianNumber(activity.average_rating).replace(',00', ',0')}
                </span>
                <span className="ml-1 text-sm text-gray-500">
                  ({activity.total_ratings})
                </span>
              </div>

              {/* Duration */}
              {activity.estimatedDuration && (
                <div className="flex items-center text-sm text-gray-500">
                  <Clock className="w-4 h-4 mr-1" />
                  <span>{activity.estimatedDuration}</span>
                </div>
              )}
            </div>

            {/* Max participants */}
            {activity.max_participants && (
              <div className="flex items-center text-sm text-gray-500">
                <Users className="w-4 h-4 mr-1" />
                <span>atÃ© {activity.max_participants}</span>
              </div>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/journey-progress-t.content:
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CheckCircle, MapPin, Plane, Camera, Star, Trophy, Sparkles, Calendar, Users, DollarSign } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Button } from '@/components/ui/button';

export interface JourneyMilestone {
  id: string;
  title: string;
  description: string;
  icon: React.ReactNode;
  completed: boolean;
  completedAt?: Date;
  points?: number;
  category: 'planning' | 'booking' | 'experience' | 'completion';
}

interface JourneyProgressTrackerProps {
  tripId: string;
  milestones: JourneyMilestone[];
  onMilestoneComplete?: (milestoneId: string) => void;
  className?: string;
}

const categoryColors = {
  planning: 'bg-blue-500',
  booking: 'bg-green-500',
  experience: 'bg-purple-500',
  completion: 'bg-yellow-500'
};

const categoryLabels = {
  planning: 'Planejamento',
  booking: 'Reservas',
  experience: 'ExperiÃªncia',
  completion: 'ConclusÃ£o'
};

export function JourneyProgressTracker({
  tripId,
  milestones,
  onMilestoneComplete,
  className = ""
}: JourneyProgressTrackerProps) {
  const [celebratingMilestone, setCelebratingMilestone] = useState<string | null>(null);
  const [totalPoints, setTotalPoints] = useState(0);
  const [showAnimation, setShowAnimation] = useState(false);

  const completedMilestones = milestones.filter(m => m.completed);
  const progressPercentage = (completedMilestones.length / milestones.length) * 100;

  useEffect(() => {
    const newTotalPoints = completedMilestones.reduce((sum, m) => sum + (m.points || 0), 0);
    if (newTotalPoints > totalPoints) {
      setTotalPoints(newTotalPoints);
      setShowAnimation(true);
      setTimeout(() => setShowAnimation(false), 2000);
    }
  }, [completedMilestones, totalPoints]);

  const handleMilestoneComplete = (milestoneId: string) => {
    setCelebratingMilestone(milestoneId);
    setTimeout(() => setCelebratingMilestone(null), 3000);
    onMilestoneComplete?.(milestoneId);
  };

  const getMilestonesByCategory = (category: string) => {
    return milestones.filter(m => m.category === category);
  };

  const getCategoryProgress = (category: string) => {
    const categoryMilestones = getMilestonesByCategory(category);
    const completedInCategory = categoryMilestones.filter(m => m.completed);
    return categoryMilestones.length > 0 ? (completedInCategory.length / categoryMilestones.length) * 100 : 0;
  };

  return (
    <Card className={`w-full ${className}`}>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <MapPin className="h-5 w-5" />
            Journey Progress
          </CardTitle>
          <div className="flex items-center gap-2">
            <motion.div
              animate={showAnimation ? { scale: [1, 1.2, 1] } : {}}
              transition={{ duration: 0.5 }}
            >
              <Badge variant="secondary" className="flex items-center gap-1">
                <Star className="h-3 w-3" />
                {totalPoints} points
              </Badge>
            </motion.div>
            {showAnimation && (
              <motion.div
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="text-yellow-500"
              >
                <Sparkles className="h-4 w-4" />
              </motion.div>
            )}
          </div>
        </div>
        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm">
            <span>Progresso Geral</span>
            <span>{completedMilestones.length}/{milestones.length} concluÃ­dos</span>
          </div>
          <Progress value={progressPercentage} className="h-2" />
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Category Progress */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          {Object.entries(categoryLabels).map(([key, label]) => (
            <div key={key} className="text-center">
              <div className="mb-2">
                <div className={`w-8 h-8 rounded-full ${categoryColors[key as keyof typeof categoryColors]} mx-auto mb-1 flex items-center justify-center`}>
                  <div className="w-4 h-4 bg-white rounded-full" />
                </div>
                <div className="text-xs text-muted-foreground">{label}</div>
              </div>
              <Progress 
                value={getCategoryProgress(key)} 
                className="h-1"
              />
              <div className="text-xs text-muted-foreground mt-1">
                {getMilestonesByCategory(key).filter(m => m.completed).length}/{getMilestonesByCategory(key).length}
              </div>
            </div>
          ))}
        </div>

        {/* Milestones List */}
        <div className="space-y-4">
          {Object.entries(categoryLabels).map(([categoryKey, categoryLabel]) => {
            const categoryMilestones = getMilestonesByCategory(categoryKey);
            if (categoryMilestones.length === 0) return null;

            return (
              <div key={categoryKey} className="space-y-2">
                <h4 className="font-medium text-sm flex items-center gap-2">
                  <div className={`w-3 h-3 rounded-full ${categoryColors[categoryKey as keyof typeof categoryColors]}`} />
                  {categoryLabel}
                </h4>
                <div className="space-y-2 pl-5">
                  {categoryMilestones.map((milestone, index) => (
                    <motion.div
                      key={milestone.id}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      className={`flex items-center gap-3 p-3 rounded-lg border transition-all ${
                        milestone.completed 
                          ? 'bg-green-50 border-green-200 dark:bg-green-900/20 dark:border-green-800' 
                          : 'bg-gray-50 border-gray-200 dark:bg-gray-900/20 dark:border-gray-800'
                      }`}
                    >
                      <motion.div
                        animate={celebratingMilestone === milestone.id ? { rotate: 360, scale: [1, 1.3, 1] } : {}}
                        transition={{ duration: 0.6 }}
                      >
                        {milestone.completed ? (
                          <CheckCircle className="h-5 w-5 text-green-600" />
                        ) : (
                          <div className="w-5 h-5 rounded-full border-2 border-gray-300" />
                        )}
                      </motion.div>

                      <div className="flex-1">
                        <div className="flex items-center gap-2">
                          {milestone.icon}
                          <h5 className={`font-medium ${
                            milestone.completed ? 'text-green-800 dark:text-green-200' : 'text-gray-700 dark:text-gray-300'
                          }`}>
                            {milestone.title}
                          </h5>
                          {milestone.points && (
                            <Badge variant="outline" className="text-xs">
                              +{milestone.points} pts
                            </Badge>
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground mt-1">
                          {milestone.description}
                        </p>
                        {milestone.completedAt && (
                          <p className="text-xs text-muted-foreground mt-1">
                            Completed: {milestone.completedAt.toLocaleDateString()}
                          </p>
                        )}
                      </div>

                      {!milestone.completed && (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleMilestoneComplete(milestone.id)}
                          className="ml-2"
                        >
                          Mark Complete
                        </Button>
                      )}
                    </motion.div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>

        {/* Celebration Animation */}
        <AnimatePresence>
          {celebratingMilestone && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              className="fixed inset-0 flex items-center justify-center bg-black/50 z-50 pointer-events-none"
            >
              <motion.div
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
