import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { 
  MapPin, 
  Users, 
  DollarSign, 
  Calendar, 
  MessageCircle, 
  Star,
  ArrowRight,
  ArrowLeft,
  X,
  CheckCircle,
  Sparkles,
  Target,
  Heart,
  Shield
} from 'lucide-react';

interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  icon: React.ReactNode;
  target?: string;
  position: 'top' | 'bottom' | 'left' | 'right' | 'center';
  highlight?: boolean;
  action?: {
    label: string;
    href?: string;
    onClick?: () => void;
  };
}

interface OnboardingTourProps {
  isOpen: boolean;
  onClose: () => void;
  onComplete: () => void;
  userPreferences?: {
    travel_style?: string;
    interests?: string[];
    experience?: 'iniciante' | 'intermediario' | 'experiente';
  };
}

const getPersonalizedSteps = (preferences?: OnboardingTourProps['userPreferences']): OnboardingStep[] => {
  const baseSteps: OnboardingStep[] = [
    {
      id: 'welcome',
      title: 'Bem-vindo ao ViajaJunto! ðŸŽ‰',
      description: 'Estamos animados para te ajudar a descobrir o mundo de forma mais econÃ´mica e divertida!',
      icon: <Sparkles className="w-6 h-6 text-yellow-400" />,
      position: 'center',
      highlight: true
    },
    {
      id: 'concept',
      title: 'Como Funciona',
      description: 'Conecte-se com viajantes que compartilham seus interesses e divida custos de hospedagem, transporte e atividades.',
      icon: <Users className="w-6 h-6 text-blue-400" />,
      position: 'center'
    },
    {
      id: 'savings',
      title: 'Economize atÃ© 65%',
      description: 'Nossos usuÃ¡rios economizam em mÃ©dia 65% dividindo custos de viagem com outros viajantes.',
      icon: <DollarSign className="w-6 h-6 text-green-400" />,
      position: 'center'
    },
    {
      id: 'search',
      title: 'Explore Viagens',
      description: 'Use nossa busca avanÃ§ada para encontrar viagens que combinam com seu perfil e orÃ§amento.',
      icon: <MapPin className="w-6 h-6 text-purple-400" />,
      position: 'top',
      target: '[data-tour="search-button"]',
      action: {
        label: 'Ver Busca',
        href: '/search'
      }
    },
    {
      id: 'create',
      title: 'Crie Sua Viagem',
      description: 'Planeje sua prÃ³pria aventura e convide outros viajantes para se juntarem a vocÃª.',
      icon: <Calendar className="w-6 h-6 text-orange-400" />,
      position: 'top',
      target: '[data-tour="create-button"]',
      action: {
        label: 'Criar Viagem',
        href: '/create-trip'
      }
    },
    {
      id: 'safety',
      title: 'SeguranÃ§a em Primeiro Lugar',
      description: 'Todos os perfis sÃ£o verificados e temos um sistema de avaliaÃ§Ãµes para garantir sua seguranÃ§a.',
      icon: <Shield className="w-6 h-6 text-red-400" />,
      position: 'center'
    }
  ];

  // Personalizar baseado nas preferÃªncias do usuÃ¡rio
  if (preferences?.travel_style) {
    const styleMessages = {
      'aventura': 'Perfeito para aventureiros! VocÃª encontrarÃ¡ muitos companheiros para trilhas, escaladas e esportes radicais.',
      'praia': 'Ã“tima escolha! Temos muitas viagens para localidades paradisÃ­acas e resorts.',
      'cultura': 'Excelente! Conecte-se com viajantes que amam museus, arquitetura e experiÃªncias culturais.',
      'natureza': 'Maravilhoso! Encontre companheiros para parques nacionais e ecoturismo.',
      'urbanas': 'Perfeito! Explore grandes cidades com outros viajantes urbanos.'
    };

    baseSteps.splice(2, 0, {
      id: 'personalized',
      title: `Viagens de ${preferences.travel_style}`,
      description: styleMessages[preferences.travel_style as keyof typeof styleMessages] || 'Encontraremos viagens perfeitas para seu estilo!',
