const sortOptions = [
  { value: 'relevance', label: 'Mais Relevantes' },
  { value: 'date', label: 'Data de InÃ­cio' },
  { value: 'budget-low', label: 'Menor PreÃ§o' },
  { value: 'budget-high', label: 'Maior PreÃ§o' },
  { value: 'rating', label: 'Melhor AvaliaÃ§Ã£o' },
