              <Label htmlFor="end-date">Data de Fim</Label>
              <Input
                id="end-date"
                type="date"
                value={filters.endDate}
                onChange={(e) => updateFilters({ endDate: e.target.value })}
              />
            </div>
          </CollapsibleContent>
        </Collapsible>

        <Separator />

        {/* Budget */}
        <Collapsible
