  const travelStyleLabels: { [key: string]: string } = {
    praia: "Praia",
    neve: "Neve",
    cruzeiros: "Cruzeiros",
    natureza: "Natureza e Ecoturismo",
    cultural: "Culturais e HistÃ³ricas",
    aventura: "Aventura",
    parques: "Parques TemÃ¡ticos",
    urbanas: "Viagens Urbanas / Cidades Grandes",
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
