import { useState, useEffect } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth-snake";
import { ProtectedRoute } from "@/lib/protected-route";
import { Navbar } from "@/components/navbar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { DestinationSelector } from "@/components/destination-selector";
import { 
  ArrowLeft, 
  Save, 
  MapPin, 
  Calendar, 
  DollarSign, 
  Users, 
  Edit, 
  Trash2,
  AlertTriangle
} from "lucide-react";
import { getRealParticipantsCount } from "@/lib/trip-utils";

export default function EditTripPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();
  const params = useParams();
  const tripId = parseInt(params.id as string);

  const [formData, setFormData] = useState({
    title: '',
    destination: '',
    description: '',
    start_date: '',
    end_date: '',
    budget: '',
    max_participants: '',
    travel_style: ''
  });
  const [selectedDestinationId, setSelectedDestinationId] = useState<number | undefined>(undefined);

  // Fetch trip data
  const { data: trip, isLoading: tripLoading, error: tripError } = useQuery({
    queryKey: ['/api/trips', tripId],
    queryFn: async () => {
      const response = await apiRequest('GET', `/api/trips/${tripId}`);
      return response.json();
    },
    enabled: !!tripId
  });

  // Update form when trip data loads
  useEffect(() => {
    if (trip) {
      // Check if user is the creator
      if (trip.creator_id !== user?.id) {
        toast({
          variant: "destructive",
          title: "Acesso negado",
          description: "Apenas o criador da viagem pode editÃ¡-la."
        });
        setLocation('/dashboard');
        return;
      }

      setFormData({
        title: trip.title || '',
        destination: trip.destination || '',
        description: trip.description || '',
        start_date: trip.start_date ? new Date(trip.start_date).toISOString().split('T')[0] : '',
        end_date: trip.end_date ? new Date(trip.end_date).toISOString().split('T')[0] : '',
        budget: trip.budget?.toString() || '',
        max_participants: trip.max_participants?.toString() || '',
        travel_style: trip.travel_style || ''
      });

      // Set selected destination ID
      if (trip.destination_id) {
        setSelectedDestinationId(trip.destination_id);
      }
    }
  }, [trip, user?.id, setLocation, toast]);

  // Update trip mutation
  const updateTripMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('PATCH', `/api/trips/${tripId}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Viagem atualizada!",
        description: "As alteraÃ§Ãµes foram salvas com sucesso."
      });
      queryClient.invalidateQueries({ queryKey: ['/api/trips'] });
      queryClient.invalidateQueries({ queryKey: ['/api/my-trips'] });
      setLocation('/dashboard');
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao atualizar viagem",
        description: error.message || "Tente novamente mais tarde."
      });
    }
  });

  // Delete trip mutation
  const deleteTripMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('DELETE', `/api/trips/${tripId}`);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Viagem excluÃ­da",
        description: "A viagem foi removida permanentemente."
      });
      queryClient.invalidateQueries({ queryKey: ['/api/trips'] });
      queryClient.invalidateQueries({ queryKey: ['/api/my-trips'] });
      setLocation('/dashboard');
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Erro ao excluir viagem",
        description: error.message || "Tente novamente mais tarde."
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validation
    if (!formData.title.trim() || !formData.destination.trim() || !formData.start_date || !formData.end_date) {
      toast({
        variant: "destructive",
        title: "Campos obrigatÃ³rios",
        description: "Por favor, preencha todos os campos obrigatÃ³rios."
      });
      return;
    }

    if (new Date(formData.start_date) >= new Date(formData.end_date)) {
      toast({
        variant: "destructive",
        title: "Datas invÃ¡lidas",
        description: "A data de fim deve ser posterior Ã  data de inÃ­cio."
      });
      return;
    }

    const data = {
      ...formData,
      destination_id: selectedDestinationId,
      budget: formData.budget ? parseInt(formData.budget) : 0,
      max_participants: formData.max_participants ? parseInt(formData.max_participants) : 2,
      // Convert date strings to ISO format for backend
      start_date: formData.start_date ? new Date(formData.start_date).toISOString() : undefined,
      end_date: formData.end_date ? new Date(formData.end_date).toISOString() : undefined
    };

    updateTripMutation.mutate(data);
  };

  const handleDelete = () => {
    if (getRealParticipantsCount(trip) > 1) {
      toast({
        variant: "destructive",
        title: "NÃ£o Ã© possÃ­vel excluir",
        description: "A viagem tem outros participantes. Use 'Cancelar' no dashboard para transferir a organizaÃ§Ã£o."
      });
      return;
    }

    const confirmed = window.confirm(
      `Tem certeza que deseja excluir permanentemente a viagem "${trip?.title}"? Esta aÃ§Ã£o nÃ£o pode ser desfeita.`
    );

    if (confirmed) {
      deleteTripMutation.mutate();
    }
  };

  if (tripLoading) {
    return (
      <ProtectedRoute>
        <div className="min-h-screen bg-gray-50">
          <Navbar />
          <div className="container mx-auto px-4 py-8">
            <div className="max-w-2xl mx-auto">
              <div className="animate-pulse">
                <div className="h-8 bg-gray-200 rounded w-1/3 mb-6"></div>
                <div className="bg-white rounded-lg shadow p-6">
                  <div className="space-y-4">
                    <div className="h-4 bg-gray-200 rounded w-full"></div>
                    <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                    <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </ProtectedRoute>
    );
  }

  if (tripError || !trip) {
    return (
      <ProtectedRoute>
        <div className="min-h-screen bg-gray-50">
          <Navbar />
          <div className="container mx-auto px-4 py-8">
            <div className="max-w-2xl mx-auto text-center">
              <h1 className="text-2xl font-bold text-gray-900 mb-4">Viagem nÃ£o encontrada</h1>
              <Button onClick={() => setLocation('/dashboard')}>
                <ArrowLeft className="h-4 w-4 mr-2" />
                Voltar ao Dashboard
              </Button>
            </div>
          </div>
        </div>
      </ProtectedRoute>
    );
  }

  return (
    <ProtectedRoute>
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
        <Navbar />

        <div className="container mx-auto px-4 py-8">
          <div className="max-w-4xl mx-auto">
            {/* Header */}
            <div className="flex items-center gap-4 mb-8">
              <Button 
                variant="outline" 
                onClick={() => setLocation('/dashboard')}
