  ];

  const monthNames = [
    'Janeiro', 'Fevereiro', 'MarÃ§o', 'Abril', 'Maio', 'Junho',
    'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
  ];

  const colors = [
    'bg-cyan-100 text-cyan-800', 'bg-pink-100 text-pink-800', 'bg-green-100 text-green-800',
