import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Bell, X, CheckCircle, AlertCircle, Info, UserPlus, Calendar, DollarSign } from 'lucide-react';
import { Button } from './button';
import { Card, CardContent } from './card';
import { Badge } from './badge';

interface Notification {
  id: string;
  type: 'trip_request' | 'trip_accepted' | 'trip_rejected' | 'new_message' | 'budget_update' | 'system' | 'success' | 'error' | 'info';
  title: string;
  message: string;
  timestamp: Date;
  read: boolean;
  actionUrl?: string;
  actionText?: string;
}

const notificationIcons = {
  trip_request: UserPlus,
  trip_accepted: CheckCircle,
  trip_rejected: X,
  new_message: Bell,
  budget_update: DollarSign,
  system: Info,
  success: CheckCircle,
  error: AlertCircle,
  info: Info,
};

const notificationColors = {
  trip_request: 'bg-blue-50 border-blue-200 text-blue-800',
  trip_accepted: 'bg-green-50 border-green-200 text-green-800',
  trip_rejected: 'bg-red-50 border-red-200 text-red-800',
  new_message: 'bg-purple-50 border-purple-200 text-purple-800',
  budget_update: 'bg-yellow-50 border-yellow-200 text-yellow-800',
  system: 'bg-gray-50 border-gray-200 text-gray-800',
  success: 'bg-green-50 border-green-200 text-green-800',
  error: 'bg-red-50 border-red-200 text-red-800',
  info: 'bg-blue-50 border-blue-200 text-blue-800',
};

export function NotificationSystem() {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);

  // Simulated notifications (in a real app, these would come from your API)
  useEffect(() => {
    const sampleNotifications: Notification[] = [
      {
        id: '1',
        type: 'trip_request',
        title: 'Nova solicitaÃ§Ã£o de viagem',
        message: 'JoÃ£o Silva quer participar da sua viagem para Machu Picchu',
        timestamp: new Date(Date.now() - 5 * 60 * 1000),
        read: false,
        actionUrl: '/trip/1',
        actionText: 'Ver solicitaÃ§Ã£o'
      },
      {
        id: '2',
        type: 'trip_accepted',
        title: 'SolicitaÃ§Ã£o aceita!',
        message: 'Sua solicitaÃ§Ã£o para a viagem "Aventura no Pantanal" foi aceita',
        timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000),
        read: false,
        actionUrl: '/trip/2',
        actionText: 'Ver viagem'
      },
      {
        id: '3',
        type: 'budget_update',
        title: 'OrÃ§amento atualizado',
        message: 'O orÃ§amento da viagem "Carnaval em Salvador" foi atualizado para R$ 2.500',
        timestamp: new Date(Date.now() - 24 * 60 * 60 * 1000),
        read: true,
        actionUrl: '/trip/3',
        actionText: 'Ver detalhes'
      },
    ];

    setNotifications(sampleNotifications);
  }, []);

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = (id: string) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, read: true } : n)
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, read: true })));
  };

  const removeNotification = (id: string) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  const formatTimestamp = (timestamp: Date) => {
    const now = new Date();
    const diff = now.getTime() - timestamp.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(diff / 3600000);
    const days = Math.floor(diff / 86400000);

    if (minutes < 1) return 'Agora mesmo';
    if (minutes < 60) return `${minutes}m atrÃ¡s`;
    if (hours < 24) return `${hours}h atrÃ¡s`;
    return `${days}d atrÃ¡s`;
  };

  return (
    <div className="relative">
      {/* Notification Bell */}
      <Button
        variant="ghost"
        size="sm"
        className="relative p-2"
        onClick={() => setIsOpen(!isOpen)}
      >
        <Bell className="h-5 w-5" />
        {unreadCount > 0 && (
          <Badge className="absolute -top-1 -right-1 h-5 w-5 text-xs bg-red-500 hover:bg-red-600">
            {unreadCount}
          </Badge>
        )}
      </Button>

      {/* Notification Dropdown */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="absolute right-0 top-full mt-2 w-80 bg-white rounded-lg shadow-lg border border-gray-200 z-50"
          >
            <div className="p-4 border-b border-gray-100">
              <div className="flex items-center justify-between">
                <h3 className="font-semibold text-gray-900">NotificaÃ§Ãµes</h3>
                {unreadCount > 0 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={markAllAsRead}
                    className="text-xs text-blue-600 hover:text-blue-800"
                  >
                    Marcar todas como lidas
                  </Button>
                )}
              </div>
            </div>

            <div className="max-h-96 overflow-y-auto">
              {notifications.length === 0 ? (
                <div className="p-4 text-center text-gray-500">
                  Nenhuma notificaÃ§Ã£o
                </div>
              ) : (
                notifications.map((notification) => {
                  const Icon = notificationIcons[notification.type];
                  const colorClass = notificationColors[notification.type];

                  return (
                    <motion.div
                      key={notification.id}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      className={`p-4 border-b border-gray-100 hover:bg-gray-50 transition-colors ${
                        !notification.read ? 'bg-blue-50/50' : ''
                      }`}
                    >
                      <div className="flex items-start gap-3">
                        <div className={`p-2 rounded-full ${colorClass}`}>
                          <Icon className="h-4 w-4" />
                        </div>

                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <h4 className="font-medium text-gray-900 text-sm">
                              {notification.title}
                            </h4>
                            <button
                              onClick={() => removeNotification(notification.id)}
                              className="text-gray-400 hover:text-gray-600"
                            >
                              <X className="h-4 w-4" />
                            </button>
                          </div>

                          <p className="text-sm text-gray-600 mt-1">
                            {notification.message}
                          </p>

                          <div className="flex items-center justify-between mt-2">
                            <span className="text-xs text-gray-500">
                              {formatTimestamp(notification.timestamp)}
                            </span>

                            {notification.actionUrl && (
                              <Button
                                variant="ghost"
                                size="sm"
                                className="text-xs text-blue-600 hover:text-blue-800"
                                onClick={() => {
                                  markAsRead(notification.id);
                                  // Navigate to action URL
                                  window.location.href = notification.actionUrl!;
                                }}
                              >
                                {notification.actionText}
                              </Button>
                            )}
                          </div>
                        </div>
                      </div>
                    </motion.div>
                  );
                })
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Backdrop */}
      {isOpen && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
}

client/src/components/ui/optimized-image.content:
import { useState, useEffect, useRef, useCallback, memo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ImageIcon, Loader2 } from 'lucide-react';

interface OptimizedImageProps extends React.ImgHTMLAttributes<HTMLImageElement> {
  src: string;
  alt: string;
  fallbackSrc?: string;
  className?: string;
  lazy?: boolean;
  blurhash?: string;
  onLoad?: () => void;
  onError?: () => void;
}

export const OptimizedImage = memo(function OptimizedImage({
  src,
  alt,
  fallbackSrc = 'https://images.unsplash.com/photo-1469474968028-56623f02e42e?w=800&q=80',
  className = '',
  lazy = true,
  blurhash,
  onLoad,
  onError,
  ...props
}: OptimizedImageProps) {
  const [isLoaded, setIsLoaded] = useState(false);
  const [isError, setIsError] = useState(false);
  const [isInView, setIsInView] = useState(!lazy);
  const [currentSrc, setCurrentSrc] = useState(src);
  const imgRef = useRef<HTMLImageElement>(null);
  const observerRef = useRef<IntersectionObserver | null>(null);

  // Intersection observer for lazy loading
  useEffect(() => {
    if (!lazy) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsInView(true);
          if (observerRef.current) {
            observerRef.current.disconnect();
          }
        }
      },
      {
        threshold: 0.1,
        rootMargin: '50px'
      }
    );

    observerRef.current = observer;

    if (imgRef.current) {
      observer.observe(imgRef.current);
    }

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, [lazy]);

  const handleLoad = useCallback(() => {
    setIsLoaded(true);
    onLoad?.();
  }, [onLoad]);

  const handleError = useCallback(() => {
    if (currentSrc !== fallbackSrc) {
      setCurrentSrc(fallbackSrc);
      setIsError(false);
    } else {
      setIsError(true);
    }
    onError?.();
  }, [currentSrc, fallbackSrc, onError]);

  // Preload image when in view
  useEffect(() => {
    if (!isInView) return;

    const img = new Image();
    img.src = currentSrc;
    img.onload = handleLoad;
    img.onerror = handleError;

    return () => {
      img.onload = null;
      img.onerror = null;
    };
  }, [isInView, currentSrc, handleLoad, handleError]);

  // Blurhash placeholder (if provided)
  const BlurhashPlaceholder = () => (
    <div 
      className={`bg-gray-100 animate-pulse ${className}`}
      style={{ 
        backgroundImage: blurhash ? `url(${blurhash})` : undefined,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    />
  );

  // Loading skeleton
  const LoadingSkeleton = () => (
    <div className={`bg-gray-100 animate-pulse flex items-center justify-center ${className}`}>
      <Loader2 className="h-8 w-8 text-gray-400 animate-spin" />
    </div>
  );

  // Error state
  const ErrorState = () => (
    <div className={`bg-gray-100 flex items-center justify-center ${className}`}>
      <div className="text-center">
        <ImageIcon className="h-8 w-8 text-gray-400 mx-auto mb-2" />
        <span className="text-xs text-gray-500">Falha ao carregar</span>
      </div>
    </div>
  );

  if (isError) {
    return <ErrorState />;
  }

  return (
    <div className={`relative overflow-hidden ${className}`}>
      <AnimatePresence mode="wait">
        {!isLoaded && (
          <motion.div
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="absolute inset-0"
          >
            {blurhash ? <BlurhashPlaceholder /> : <LoadingSkeleton />}
          </motion.div>
        )}
      </AnimatePresence>

      {isInView && (
        <motion.img
          ref={imgRef}
          src={currentSrc}
          alt={alt}
          className={`w-full h-full object-cover ${className}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: isLoaded ? 1 : 0 }}
          transition={{ duration: 0.3 }}
          onLoad={handleLoad}
          onError={handleError}
          {...props}
        />
      )}
    </div>
  );
});

// Progressive image loading with multiple sources
export const ProgressiveImage = memo(function ProgressiveImage({
  src,
  lowQualitySrc,
  alt,
  className = '',
  ...props
}: {
  src: string;
  lowQualitySrc?: string;
  alt: string;
  className?: string;
} & React.ImgHTMLAttributes<HTMLImageElement>) {
  const [isHighQualityLoaded, setIsHighQualityLoaded] = useState(false);
  const [isLowQualityLoaded, setIsLowQualityLoaded] = useState(false);

  useEffect(() => {
    // Load low quality image first
    if (lowQualitySrc) {
      const lowQualityImg = new Image();
      lowQualityImg.src = lowQualitySrc;
      lowQualityImg.onload = () => setIsLowQualityLoaded(true);
    }

    // Load high quality image
    const highQualityImg = new Image();
    highQualityImg.src = src;
    highQualityImg.onload = () => setIsHighQualityLoaded(true);
  }, [src, lowQualitySrc]);

  return (
    <div className={`relative overflow-hidden ${className}`}>
      {/* Low quality placeholder */}
      {lowQualitySrc && (
        <motion.img
          src={lowQualitySrc}
          alt={alt}
          className={`absolute inset-0 w-full h-full object-cover filter blur-sm scale-110 ${className}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: isLowQualityLoaded && !isHighQualityLoaded ? 1 : 0 }}
          transition={{ duration: 0.3 }}
          {...props}
        />
      )}

      {/* High quality image */}
      <motion.img
        src={src}
        alt={alt}
        className={`w-full h-full object-cover ${className}`}
        initial={{ opacity: 0 }}
        animate={{ opacity: isHighQualityLoaded ? 1 : 0 }}
        transition={{ duration: 0.3 }}
        {...props}
      />

      {/* Loading skeleton */}
      {!isLowQualityLoaded && !isHighQualityLoaded && (
        <div className={`absolute inset-0 bg-gray-100 animate-pulse flex items-center justify-center ${className}`}>
          <Loader2 className="h-8 w-8 text-gray-400 animate-spin" />
        </div>
      )}
    </div>
  );
});

